css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.htcmania.com/showthread.php") {
    #vbpostrow_ > td, #threadslist, 
    #threadslist > tbody, #threadslist > tbody > tr , 
    #threadslist td, div.page, td.alt1 {
        /*
        background-color: black !important;
        color: #E7E9E9 !important;
        */        
    }
    
    td.alt2{
        /*
        background-color: #333 !important;
        color: #aaa !important;
        */
    }
    
    .thead {
        /*
        background-color: #444 !important;
        background-image: none !important;
        color: white !important;    
        */
    }
    
    a {
        /*
        color: #09f !important;
        */
    }
    
    td[nowrap] div.smallfont {
        display: none !important;
    }
    
    form[action="portal.php"] {
        display:none !important;
    }
    
    *[title^="Avatar"] {
        width: auto !important;
        height: 2em !important;
        display: inline !important;
    }
    
    
    
    .bigusername {
    }
    
    .bigusername + img[alt~="l√≠nea"] {
        width: 1em !important;
    }
    
    body > table {
        display: none !important;
    }
    
    
    
    #breadcrumbNavbar > tbody > tr > td:first-child {
        display: block !important;
    }
    
    #breadcrumbNavbar > tbody > tr > td {
        display: none !important;
    }
    
    div.page > table.tborder:nth-child(3) {
        display: none !important;
    }
    
    div.page > div > table.tborder:nth-child(6), div.page > div > table.tborder:nth-child(1) {
        display: none !important;
    }
    
    #posts + div > div.page > div > table.tborder {
        display: none !important;
    }
    
    div[id^="post_message_"] + div:not([align]) {
        display: none !important;
    }
    
    #posts + div > div > div > table ~ * {
        display: none !important;
    }
    
    table, tr, td, tbody {
        display: inherit !important;
    }
    
    table[cellspacing="6"], table[cellspacing="6"] tr, table[cellspacing="6"] td, table[cellspacing="6"] tbody  {
        display: inline !important;
    }
    
    table[cellspacing="6"] div[id^="postmenu"]   {
        display: inline!important;
    }
    
    div[id^="post_thanks_box_"] {
        text-align: right !important;
    }

    .pagenav table, .pagenav tr, .pagenav td, .pagenav tbody {
        display: inline !important;
    }

}
