#right_column .controls_section li .followers:hover:after,
#right_column .controls_section li .drafts:hover:after,
#right_column .controls_section li a.likes:hover:after,
#right_column .controls_section li a.following:hover:after,
#right_column .controls_section li a.spotlight:hover:after,
#right_column .controls_section li a.messages:hover:after,
#right_column .controls_section li a.delete_all_messages:hover:after,
#dashboard_controls_open_blog li a.members:hover:after {
  opacity: 1 !important;
}

#right_column .controls_section li .posts:after {
  background-position: 0px 0px !important;
}
#right_column .controls_section li .followers:after,
#dashboard_controls_open_blog li a.members:after {
  background-position: -30px 0px !important;
}
#right_column .controls_section li .drafts:after {
  background-position: -60px 0px !important;
}

#right_column .controls_section li a.likes:after {
  background-position: -90px 0px !important;
}
#right_column .controls_section li a.following:after {
  background-position: 0px -25px !important;
  top: 3px !important;
  left: -1px !important;
}
#right_column .controls_section li a.spotlight:after {
  background-position: -30px -25px !important;
  top: 3px !important;
  left: 3px !important;
}
#dashboard_inbox #right_column .controls_section li a.messages:after,
#dashboard_submissions #right_column .controls_section li a.messages:after {
  background-position: -60px -25px !important;
}


#dashboard_inbox #right_column .controls_section li a.delete_all_messages:after,
#dashboard_submissions #right_column .controls_section li a.delete_all_messages:after {
  background-position: -90px -25px !important;
