css:
@-moz-document domain("mail.yahoo.com"){

#shellcontent{
right:0 !important;}

.list-header-wrapper {
border:0px !important;}

#theAd, #theMNWAd, #slot_MON, #slot_LREC, #slot_MIP, #slot_MON_sent, #yuhead-promo, 
.yuhead-com-link-item, .yucs-sweb-btn, #textbox, .yucs-opi.sp.invisible, 
#yucs-search_button, #yucs-top-bar, #yucs-web_search_button_wrapper {
display:none!important;}

DIV[data-action="select-message"] .flag {
margin-top:3px !important;}

DIV[data-action="select-message"] .cbox,
DIV[data-action="select-message"] .msg-lst-srch {
margin-top:2px !important;}

DIV[data-action="select-message"] .icon.delete-msg {
margin-top:5px !important;}

.yucs-form-btn #yucs-prop_search_button_wrapper {
background:none !important;
box-shadow:none !important;}

#applist {
min-width:1210px !important;}

.yucs-fl-right {
margin-top:2px !important;}

#yucs-search {
left:600px !important;
width:407px !important;}

#mnp-search_box {
border-right:0px !important;
width:270px !important;}

.yucs-search-input {
height:26px !important;
border-top-right-radius:0px !important;
border-bottom-right-radius:0px !important;}

#yucs-sprop_button {
height:26px !important;
padding-top:3px !important;
border-top-left-radius:0px !important;
border-bottom-left-radius:0px !important;
margin-left:-18px !important;
border-left:0px !important;
margin-top:3px !important;
box-shadow:none !important;
width:90px !important;}

.yucs-fl-left {
z-index:1 !important;}

.nav-bar {
margin-left:80px !important;
top:12px !important;
background:none !important;
border-bottom:0px !important;
width:40% !important;
z-index:1 !important;}

A[role="tab"][aria-selected="false"]:hover {
padding-bottom:5px !important;}

#yucs #yucs-logo div {
background-image:url("http://i43.tinypic.com/2agiyqp.png") !important;
width:95px !important;
height:23px !important;
margin:5px 0 0 5px !important;
background-repeat:no-repeat !important;}

#yucs-logo h2 {
height:0px !important;}

#shellnavigation, #shellcontent {
top:85px !important;}

.panescroll #subshellnavigation, .content.withsubshellnavigation {
left:-1px !important;}

#contacts-content {
top:-49px !important;}

#subshellnavigation.list-view {
top:-50px !important;}

#container {
top:0px !important;
background-color:#FFF !important;}

.panescroll.headless #shellnavigation {
top:36px !important;}

#main {
top:0px !important;
position:relative !important;}

.unremovable.active, .removable.active {
border-radius:0px !important;}

.removable.active B, #tablist > .removable {
text-transform:uppercase !important;}

* {
font-family:Calibri !important;}

#btn-reply > A, #btn-move > A, #btn-ml-preview > A, #btn-actions > A, #btn-new > A,
#btn-contact-actions {
line-height:12px !Important;}

#yuhead-search > #yuhead-sform-cont {
border-radius:1px !important;
width:400px !important;
border:0px !important;
margin:0px !important;}

#yucs .yucs-button_theme {
padding-bottom:0px !important;}

#toolbar {
width:100% !important;}

.yui3-js-enabled {
overflow-x:hidden !important;}
}
