css:
@namespace url(http://www.w3.org/1999/xhtml);

/*  Youtube COSMIC PANDA  */
 @-moz-document
	domain(youtube.com){
	/* title restyle */
		#watch7-headline-title {height:30px!important;max-height:30px!important}
		#eow-title { font-size:24px!important;  color:#E4E4E4!important; text-transform:capitalize!important; text-shadow: #000 3px 3px 3px!important;}
		a#watch7-headline-show-title			{color:#328cb7!important; text-shadow: #000 3px 3px 6px!important}
		a#watch7-headline-show-title:focus	{color:#209bd6!important; background-color: transparent!important; outline:0px!important; -moz-outline-radius: 0px!important;}

	/* buttons */
        #watch7-headline-user-info {margin-left:825px!important;}
    


		.yt-uix-expander-collapsed.yt-uix-button.yt-uix-button-default{
		left:-30px!important;
		margin:0px!important;
		padding:0 0px 3px 3px!important;
		border-radius:6px!important;
		position:relative!important;
		height:30px!important;
		width:110px!important;
		font-size:14px!important;
		color: #e4e4e4!important;
		text-shadow: #000 3px 3px 3px!important;
		background-image: -moz-linear-gradient(center top , #555555 0pt, #333333 100%)!important;
		border-color: #1F1F1F!important;
		box-shadow: 0 0 1px #1e1e1e inset!important; }
		

		.yt-subscription-button {
		left:115px!important;
		margin:0px 0px 0px 0px!important;
		padding:0px 0px 2px 6px!important;
		border-radius:6px!important;
		position:relative!important;
		height:30px!important;
		width:30px!important;
		font-size:0px!important;
		color: #e4e4e4!important;
		text-shadow: #000 3px 3px 3px!important;
		background-image: -moz-linear-gradient(center top , #555555 0pt, #333333 100%)!important;
		border-color: #1F1F1F!important;
		box-shadow: 0 0 1px #1e1e1e inset!important; }
		
		#watch7-content{width:69%!important;float:left;}
		#watch7-sidebar {width:30%!important;float:right;}

	/* video resize */
	    #watch7-player {min-width:1280px!important;  min-height:720px!important;  margin-left:auto!important;  margin-right:auto!important;}
	    #watch7-video { margin-bottom:0px!important;}
	    #watch7-playlist-data, .watch7-playlist {width:67.28%!important;}
	    #watch7-playlist-bar-toggle-button {display:inline!important;}
		#watch7-headline-user-info {position:absolute!important; top:66px!important}
		#watch7-headline {padding:6px 0 0px 0!important}
		#watch7-sidebar {margin-top:0px!important;padding-top:10px!important}
		#watch7-main.clearfix {margin-right:auto!important;min-width:1280px!important;}
		#page.watch .guide-module-toggle-label {visibility:hidden;float:left;}
		

	/* remove unnecessary things */
	    #earth-hour-switches,
		#watch7-userbanner,
.start.yt-uix-button.yt-uix-button-default[href^="/channel"],
    	.start.yt-uix-button.yt-uix-button-default[href^="/user"],
		.yt-alert.yt-alert-info.yt-rounded,
		#ticker,
		#ytf-main-div,
		#watch-privacy-contain,
		#watch-description-extras,
		#watch-video-extra,
		#pagebottom{display:none!important}
	}
