css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://live.nicovideo.jp/search") {
.recommend_list,
#search_left .result_list:nth-of-type(7),
#search_left .result_list:nth-of-type(9),
#search_right,
.notice_msg
{display:none !important}

/*4列表示*/
#search_left {width:960px !important}
#search_left .result_list .result_item {width:222px; height:175px; float:left}

/*外枠調整*/
#sort_type_area {width:960px !important}
#option_provider_area {width:578px !important}
#option_toggle_area {position:relative; left:0px}

/*検索結果内の表示調整*/
.thmb_area,
.result_item > .thmb_area > .thmb {position:relative; top:12px; width:220px !important; width:220px !important}
.detail {position:relative; top:15px; float:none; width:218px !important; height:40px !important}
.score_area {margin-right:0px !important}
.status {position:relative; top:-77px; left:-5px; font-size:7pt !important;}
.result_item.detail {width:218px; height:171px !important}
.search_stream_title {position:relative; top:-110px; left:-5px; width:220px !important; font-size:10pt !important}
.search_stream_description {position:relative; top:-22px; left:-8px; width:230px !important; height:60px !important; font-size:9pt !important}
.community_area {position:relative; top:47px; left:-5px; font-size:7pt !important}
.more_area {position:relative; top:47px; left:7px;}
}
