css:
@-moz-document url-prefix("https://www.rememberthemilk.com/home/anish.thecoder"), url-prefix("http://www.rememberthemilk.com/home/anish.thecoder") {
@font-face {
  font-family: 'Oxygen';
  font-style: normal;
  font-weight: 300;
  src: local('Oxygen Light'), local('Oxygen-Light'), url(http://themes.googleusercontent.com/static/fonts/oxygen/v3/AwBqWF2kjhlybWamaKMPcT8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}
@font-face {
  font-family: 'Oxygen';
  font-style: normal;
  font-weight: 400;
  src: local('Oxygen'), local('Oxygen-Regular'), url(http://themes.googleusercontent.com/static/fonts/oxygen/v3/RzoNiRR1p2Mqyyz2RwqSMw.woff) format('woff');
}
@font-face {
  font-family: 'Oxygen';
  font-style: normal;
  font-weight: 700;
  src: local('Oxygen Bold'), local('Oxygen-Bold'), url(http://themes.googleusercontent.com/static/fonts/oxygen/v3/yVHpdQrmTj9Kax1tmFSx2j8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}

/* FONT */
/* Font families */
body,
input, select, textarea, 
#listtabs.xtabs li.xtab_selected a, 
#detailstabs.xtabs li.xtab_selected a{
  font-family: 'Oxygen', Arial, helvetica, sans-serif !important;
}
  
/* Font sizes */
body {font-size: 0.8em !important;}
#listwrap #midcontent {font-size: 1.2em !important; }

/* Other font defs. */
span.xtd_task_name {font-weight: normal !important;}
a:link, #viewSelector a:visited {text-decoration:none !important;}

.taskoverdue {
  text-decoration: none !important;
  color: red !important;
}


/* LAYOUT */
#content { width: 1000px !important; }
#topnav {margin-bottom:2em !important;}
#detailsbox {margin-top: 2em !important;}

#listFilter {
  font-size: 13px !important;
  padding: 4px !important;
}

.xtabs a:link, xtabs a:visited {padding: 2px 11px 0 !important;}
.xtabs li {padding-top: 3px !important;}
li.xtab_selected {padding-top: 0px !important;}


/* Hide elements */
#tools, #tools_spacer { display: none !important;}
.appfooter { display: none !important; }
#appheaderlogo { display:none !important;}


/* COLORS, STYLING */
/* backgrounds */
.xtabs li, .xtabs a:link, .xtabs a:visited {background: none !important;}

.xtabs a:link, xtabs a:visited {color: #999 !important;}
.xtabs a:hover {color: #000 !important;}
.xtab_selected,
#detailsbox .xtab_selected a {border-top:5px solid #ccc !important;}
#details #tasktabs {background-color: #F9F9F9 !important;}
#details, #detailsoverflow {background-color: #fff !important;}



/* From original styling. */
#tools, #detailswrap { border:none !important; }
#tools_spacer { border: none !important; }
#break { display: none !important; }

#listtabs, #listtabs li, #listtabs li a, 
#detailstabs, #detailstabs li, #detailstabs li a, 
#tasktabs, #tasktabs li, #tasktabs li a { background: none !important; }

#listtabs.xtabs li a, #detailstabs li a { color: #444 !important;  }
.xtabs li { border: none !important; margin: 0 !important; }
#listtabs.xtabs li.xtab_selected, 
#detailstabs.xtabs li.xtab_selected { 
  border-top: 3px solid #000 !important; 
  background: #efefef !important;  }
#listtabs.xtabs li.xtab_selected a, 
#detailstabs.xtabs li.xtab_selected a { color: #000 !important; }

#appheader { padding: 0 !important; }
#statusbox { height: 3em !important; }
tr.xtr { line-height: 3em !important; }

#taskcloud_copy, .taskcloud, 
.taskcloudbox.white_rbroundbox, .white_rbtop, 
.white_rbtop div div, .white_rbcontentwrap, 
.white_rbcontent, #taskcloudcontent_copy.taskcloudcontent, 
.white_rbbot, .white_rbbot div div { 
  background: none !important; 
  background-color: #fff !important;
}
}
