css:
@-moz-document url-prefix("http://www.scp-wiki.net/") {
strong {
    color: limegreen;
    text-decoration: underline;
}
#container,
#footer,
html,
blockquote,
table.wiki-content-table th,
.scp-image-caption {
    background-color: #2E3436 !important;
}
#content-wrap,
#page-title,
blockquote {
    color: #D3D7CF !important;
}
a {
    color: #AC0010;
}
#main-content {
    margin-left: 35px !important;
}
#side-bar {
    visibility: hidden;
    height: 0px !important;
    float: right !important;
}
.page-tags {
    margin: 40px 0;
}
}
