css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("taringa.net"){

#header {
height: 70px !important;
border-bottom: 1px solid #034260 !important;
background: url(http://i41.servimg.com/u/f41/16/71/63/22/header13.png) repeat-x top center #006595 !important;
width: 995px !important;
margin: 0 auto !important;
margin-top: 20px !important;
padding:4px !important;
border-bottom:0 !important;
-webkit-border-top-left-radius: 10px !important;
-webkit-border-top-right-radius: 10px !important;
-moz-border-radius-topleft: 10px !important;
-moz-border-radius-topright: 10px !important;
border-top-left-radius: 10px !important;
border-top-right-radius: 10px !important;
}

#footer-links:before
{
content:url(http://k34.kn3.net/60DCFE0DB.png);
}

#logo {
display: block !important;
float: left !important;
margin: 10px 30px !important;
width: 187px !important;
height: 45px !important;
position: relative !important;
left: -20px !important;
top: 5px !important;
}

.show-paises-lista{
-webkit-border-radius: 10px !important;
-moz-border-radius: 10px !important;
border-radius: 10px !important;
}

#nav ul li .bubble a, #nav ul li.new .bubble span {
border: 0 !important;
}


.bubble a{
box-shadow: 0 0 0 !important;
}

.bubble a:hover{
box-shadow: 0 0 0 !important;
}



.floatR li.active a {
background: rgba(0, 0, 0, .4) !important;
margin-top: 4px !important;
color: white !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
border: 0 !important;
}

.floatR li a {
background: rgba(0, 0, 0, .4) !important;
margin-top: 4px !important;
color: white !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
border: 0 !important;
}



#nav {
width: 995px !important;
border-left:4px solid #004a95 !important;
border-right:4px solid #004a95 !important;
margin:0 auto !important;
background: #004160 url(http://i41.servimg.com/u/f41/16/71/63/22/navbar17.png) repeat-x top left !important;
}

#nav ul li a {
display: block !important;
float: left !important;
margin-top: 2px !important;
color: black !important;
border-radius: 0 !important;
border-right: 1px solid #CCC !important;
}

#menu-section-posts a , #menu-section-comunidades a , #menu-section-juegos a , #menu-section-tops a {
background:transparent !important;
}

#nav ul #menu-section-posts:hover a , #nav ul #menu-section-comunidades:hover a , #nav ul #menu-section-musica:hover a , #nav ul #menu-section-juegos:hover a , #nav ul #menu-section-tops:hover a {
background: rgba(0, 0, 0, .1) !important;
}

#nav ul li.active a {
color: black !important;
background: rgba(0, 0, 0, .1) !important;
}

#nav ul #menu-section-musica a  {
background: transparent !important;
color: #006CA5 !important;
}

#menu-section-mi a:first-child {
background-color: rgba(0, 0, 0, .8) !important;
margin-top: 0 !important;
padding-bottom: 11px !important;
box-shadow: 0 0 1px #000;
}

#menu-section-mi a:last-child {
background-color: transparent !important;
margin-top: 0 !important;
padding-bottom: 0 !important;
box-shadow: 0 !important;
}




#menu ul li a, .menu-tabs-perfil ul li a {
display: block !important;
padding: 2px 9px 2px 9px !important;
border: 1px solid transparent !important;
font-weight: bold !important;
color: white !important;
background: #3B8BAC !important;
-webkit-border-top-left-radius: 5px !important;
-webkit-border-top-right-radius: 5px !important;
-moz-border-radius-topleft: 5px !important;
-moz-border-radius-topright: 5px !important;
border-top-left-radius: 5px !important;
border-top-right-radius: 5px !important;
}
.navbar {
height: 33px !important;
border-bottom: 1px solid #062939 !important;
border-top: 1px solid #095275 !important;
border-left: 1px solid #08415C !important;
border-right: 1px solid #08415C !important;
position: absolute !important;
right: 250px !important;
top: 75px !important;
z-index: 9999!important;
}


#search {
width: 235px !important;
position: absolute !important;
right: 100px !important;
top: 17px !important;
}

.tipsy-south {
display:none !important;
}

#nav ul.floatR {
margin-right: 60px !important;
}

#menu {
padding: 8px !important;
border-bottom: 0 !important;
background: #007394 url(http://i41.servimg.com/u/f41/16/71/63/22/navbar18.png) repeat-x top left !important;
padding-bottom: 0 !important;
border-left: 1px solid #04396F !important;
border-right: 1px solid #04396F !important;
}

#menu ul li.active a, .menu-tabs-perfil ul li.selected a {
color: #2B2B2B !important;
background: white !important;
border-top-left-radius: 5px !important;
border-top-right-radius: 5px !important;
border-bottom-left-radius: 0 !important;
border-bottom-right-radius: 0 !important;
}

#main {
width:995px !important;
border-left:4px solid #004a95 !important;
border-right:4px solid #004a95 !important;
margin:0 auto !important;
}

#sidebar {
float: right !important;
padding: 2px !important;
width: 250px !important;
}


#footer .wrapper {
padding-bottom: 0 !important;
width: 995px !important;
border-top: 0 !important;
border-left: 4px solid #004A95 !important;
border-right: 4px solid #004A95 !important;
border-bottom: 30px solid #004A95 !important;
-webkit-border-bottom-right-radius: 10px !important;
-webkit-border-bottom-left-radius: 10px !important;
-moz-border-radius-bottomright: 10px !important;
-moz-border-radius-bottomleft: 10px !important;
border-bottom-right-radius: 10px !important;
border-bottom-left-radius: 10px !important;
}

.last-action {
display:none !important;
border:0 !important;
}

#footer-links {

margin: 0 auto !important;
position: relative !important;
}

ul.footer-list {
display: block !important;
margin: 10px 0 !important;
padding: 12px !important;
text-align: center !important;
background: transparent !important;
}

.anon-box {
float: right !important;
margin: 0px 250px 0 0 !important;
position: relative !important;
z-index: 9999!important;
top: 66px !important;
}


.menu-tabs-perfil{
padding: 8px !important;
border-bottom: 0;
background: #007394 url(http://i41.servimg.com/u/f41/16/71/63/22/navbar18.png) repeat-x top left !important;
padding-bottom: 0 !important;
border-left: 1px solid #04396F !important;
border-right: 1px solid #04396F !important;
}

}
