css:
@-moz-document domain('facebook.com') {


@font-face {
  font-family: 'Ubuntu';
  font-style: normal;
  font-weight: normal;
  src: local('Ubuntu'), url('https://themes.googleusercontent.com/static/fonts/ubuntu/v4/_xyN3apAT_yRRDeqB3sPRg.woff') format('woff');
}

body {
font-family: 'Ubuntu', sans-serif;
background: whitesmoke;
color: black;
}

div {
font-family: 'Ubuntu';
}

#pageLogo a, #pageLogo a:hover, #pageLogo a:focus, #pageLogo a:active {
text-shadow: white 0 0 3px;
margin-top: -8px;
padding-top: 10px;
font-family: 'Ubuntu', sans-serif;
background: none;
text-indent: 0px;
width: 120px;
content: "facebook";
text-decoration: none;
color: white;
font-size: 20px;
padding-left: 10px;
}

#pageLogo a:before {
content:"Mybook . . . . . ";
-webkit-transition: 0.5s;
-moz-transition: 0.5s;
transition: 0.5s;
width: 120px;
}

#pageLogo a:hover {
width: 120px;
color: black;
-webkit-text-shadow: white 0 0 20px;
-moz-text-shadow: white 0 0 20px;
text-shadow: white 0 0 20px;
}

.slim #blueBar {
font-family: 'Ubuntu Condensed', sans-serif;
-webkit-box-shadow: 0 2px 2px -2px rgba(0, 0, 0, .52);
height: 43px;
background: #2b88d9; 
background: -moz-linear-gradient(top, #2b88d9 0%, #3b679e 100%); 
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#2b88d9), color-stop(100%,#3b679e)); 
background: -webkit-linear-gradient(top, #2b88d9 0%,#3b679e 100%); 
background: -o-linear-gradient(top, #2b88d9 0%,#3b679e 100%); 
background: -ms-linear-gradient(top, #2b88d9 0%,#3b679e 100%); 
}

#pageNav .navLink, #pageNav #navAccountLink {
color: white;
}

#pageNav .navItem {
color: black;
background-color: rgba(0, 0, 0, 0.0);
border: #2b88d9 2px groove;
margin-top: -2px;
-webkit-border-radius: 2px;
-moz-border-radius: 2px;
border-radius: 2px;
margin-right: 2px;
-webkit-box-shadow: blue 0px 0px 2px;
-moz-box-shadow: blue 0px 0px 2px;
box-shadow: blue 0px 0px 2px;
}

#pageNav .navItem:hover {
background-color: royalblue !important;
border: 2px groove #23538a !important;
}

#pageNav .navItem:active {
background-color: dodgerblue !important;
}

#pageNav .middleItem .navLink, #pageNav .lastItem, #pageNav .firstItem a:hover {
background-color: transparent;
}

.uiScrollableAreaGripper {
-webkit-background-clip: content-box;
-moz-background-clip: content-box;
background-clip: content-box;
background: #75c0dd; 
background: -moz-linear-gradient(left, #75c0dd 0%, #23538a 100%); 
background: -webkit-gradient(linear, left top, right top, color-stop(0%,#75c0dd), color-stop(100%,#23538a)); 
background: -webkit-linear-gradient(left, #75c0dd 0%,#23538a 100%); 
background: -o-linear-gradient(left, #75c0dd 0%,#23538a 100%); 
background: -ms-linear-gradient(left, #75c0dd 0%,#23538a 100%); 
border: 1px solid black;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
-webkit-transition: width 250ms;
-moz-transition: width 250ms;
transition: width 250ms;
width: 5px;
-webkit-box-shadow: black 0 0 3px;
-moz-box-shadow: black 0 0 3px;
box-shadow: black 0 0 3px;
}

.homeWiderContent #rightCol .home_right_column{
display: none;
}

._4rw #rightCol {
display: none;
}

.hasLeftCol #mainContainer {
margin-top: 40px;
border: 2px dotted #CCC;
}

.hasLeftCol #contentCol {
border-left: 2px dotted #CCC;
min-height: 600px;
}

.hasLeftCol .homeWiderContent div#contentArea {
margin-top: -50px;
padding-left: 18px;
padding-right: 25px;
width: 760px;
}

.fbNubFlyoutTitlebar {
font-family: 'Ubuntu Condensed', sans-serif;
padding-top: 1px;
letter-spacing: 1px;
background-color: rgba(0, 0, 0, 0.4);
border: none;
}

.fbNubFlyoutTitlebar:hover {
background-color: rgba(0, 0, 0, 0.9);
}

.fbNubButton, .fbNubButton:hover, .openToggler .fbNubButton, .fbNubFlyout, .fbNubFlyout .flyoutInner, .fbNubFlyoutHeader, .fbNubFlyoutBody, .fbNubFlyoutFooter {
background-color: rgba(255, 255, 255, 0.5);;
}

.fbNubButton, .fbNubButton:hover, .openToggler .fbNubButton, .fbNubFlyout, .fbNubFlyout .flyoutInner, .fbNubFlyoutHeader, .fbNubFlyoutBody, .fbNubFlyoutFooter:hover {
background-color: rgba(255, 255, 255, 0.9);
}

.fbMercuryChatTab .fbNubFlyoutInner {
-webkit-border-radius: 5px 5px 0 0;
-moz-border-radius: 5px 5px 0 0;
border-radius: 5px 5px 0 0;
border: 2px grey ridge;
border-bottom: 2px solid black;
}

.hasLeftCol #leftCol {
background: white;
}

.fbMercuryChatTab.opened .fbDockChatTabFlyout {
background-color: transparent;
display: block;
}

#navSearch {
margin: 3px 0 0 4px;
position: relative;
width: 302px;
}

#navSearch .uiSearchInput input {
padding-top: 2px;
width: 266px;
}

#pageNav .navLink:hover, #pageNav .navLink:focus, #pageNav .navLink:active {
background-color: transparent !important;
}

.fbJewel a.jewelButton {
color: black;
background-color: rgba(0, 0, 0, 0.0);
border: #2b88d9 0px groove;
margin-top: -2px;
}

.fbJewel a.jewelButton:active, .fbJewel a.jewelButton:focus, .fbJewel a.jewelButton:hover {
background-color: royalblue !important;
border: 0px groove #23538a !important;
box-shadow: inset black 0 0 2px;
}

}
