css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
/*  -------------------------------------------------

    MINIMAL TWITTER FOR FLUID / USERSTYLES

    by Max Fenton           @maxfenton
       Craig Mod            @cmod
       Nathan Peretic       @nathanperetic
       Aristotle Pagaltzis  @ap

    ------------------------------------------------- */


.trends, .bird-topbar-etched, .wtf-module, .site-footer, .topics, .promoted-tweet {
    display: none !important;
}

.wrapper, .wrapper-narrow, .wrapper-permalink {
    background: none !important;
    max-width: 37em !important;
}

.dashboard {
    float: none !important;
    width: 100% !important;
}

.content-main {
    float: none !important;
    width: 100% !important;
}

.global-nav {
    position: relative !important;
    width: 100% !important;
    max-width: 37em !important;
    margin: 0 auto !important;
}
.global-nav .search-input {
    width: 110px !important;
}

.pull-right, .pull-right .nav, .secondary-nav {
    float: left !important;
}

.global-nav .form-search {
    width: 165px !important;
    margin-left: -22px !important;
    margin-right: 12px !important;
}

.topbar-divider {
    display: none !important;
}

.topbar {
    background-color: #333 !important;
}

.topbar-tweet-btn {
    margin-left: 17px !important;
}

.tweet-content .tweet-box {
    width: calc(100% - 20px) !important;
}

.profile-card-inner {
    width: 40% !important;
}
}
