css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wareztuga.ws")
 {

h4.catbg, h4.catbg2 , h3.catbg , h3.catbg2 , .table_list tbody.header td.catbg
{
	background: url("http://i.imgur.com/1zfUh.png") no-repeat 100% -160px !important;
	padding-right: 18px;
    padding-left: 10px;
}

h4.titlebg, h3.titlebg
{
	background: url("http://i.imgur.com/1zfUh.png") no-repeat 100% -200px !important;
	padding-right: 9px;
}
h4.titlebg img.icon
{
	float: left;
	margin: 5px 8px 0 0;
}
.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th
{
	color: #565656;
	font-family: tahoma, helvetica, sans-serif;
	font-size: 1em;
	font-weight: bold;
	background: #a7b9cd url("http://i.imgur.com/1zfUh.png") no-repeat -10px -279px !important;
}

.titlebg, .titlebg2, tr.titlebg th, tr.titlebg td, tr.titlebg2 td
{
	color: #222;
	font-family: tahoma, helvetica, sans-serif;
	font-size: 1em;
	font-weight: bold;
	background: #e3e9ef url("http://i.imgur.com/1zfUh.png") no-repeat -10px -380px !important;
}
div.cat_bar
{
	background: #99abbf url("http://i.imgur.com/1zfUh.png") no-repeat 0 -160px !important;
	padding-left: 3px !important;
	height: 33px !important;
	/*overflow: hidden;*/
	margin-bottom: 5px !important;
    font-size: 10px !important;
}
div.title_bar
{
	background: #99abbf/*e3e9ef*/ url("http://i.imgur.com/1zfUh.png") no-repeat 0 -200px !important;
	padding-left: 9px !important;
	height: 31px !important;
	overflow: hidden !important;
	margin-bottom: 1px !important;
}

div.roundframe div.cat_bar
{
	background: #99abbf url("http://i.imgur.com/1zfUh.png") no-repeat 0 -240px !important;
}

div.roundframe div.cat_bar h3.catbg
{
	background: url("http://i.imgur.com/1zfUh.png") no-repeat 100% -240px !important; 
}
div.title_barIC
{
	background: #dadfe6 url("http://i.imgur.com/1zfUh.png") no-repeat 0 -160px !important;
	padding-left: 15px;
	height: 33px;
	overflow: hidden;
	margin-bottom: 1px;
}
div.title_barIC h4.titlebg
{
	background: url("http://i.imgur.com/1zfUh.png") no-repeat 100% -160px !important;
    font-size: 13px;
}

tr.catbg th.first_th
{
	background: #a7b9cd url("http://i.imgur.com/1zfUh.png") no-repeat 0 -279px !important;
}
tr.catbg th.last_th
{
	background: #a7b9cd url("http://i.imgur.com/1zfUh.png") no-repeat 100% -279px !important;
}
tr.titlebg th.first_th
{
	background: #e3e9ef url("http://i.imgur.com/1zfUh.png") no-repeat 0 -380px !important;
}
tr.titlebg th.last_th
{
	background: #e3e9ef url("http://i.imgur.com/1zfUh.png") no-repeat 100% -380px !important;
}

.windowbg span.topslice
{
	display: block;
	padding-left: 20px;
	background: url("http://i.imgur.com/1zfUh.png") 0 -30px no-repeat !important;
}
.windowbg span.topslice span
{
	display: block;
	background: url("http://i.imgur.com/1zfUh.png") 100% -30px no-repeat !important;
	height: 11px;
}

.windowbg span.botslice
{
	display: block;
	padding-left: 20px;
	background: url("http://i.imgur.com/1zfUh.png") 0 -40px no-repeat !important;
	font-size: 5px;
	line-height: 5px;
	margin-bottom: 0.2em;
}
.windowbg span.botslice span
{
	display: block;
	background: url("http://i.imgur.com/1zfUh.png") 100% -40px no-repeat !important;
	height: 11px;
}

