css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("smart-lab.ru") {

body {
    background: none repeat scroll 0 0 white !important;
    color: #000000;
    font: 10pt Verdana,sans-serif;
}

#inindex {
    float: left !important;
    margin: 0 !important;
    width: 50% !important;
}

.indexSidebar {
    float: right !important;
    height: auto !important;
    margin-top: 0 !important;
    width: 24% !important;
}

#wrapper {
    margin: auto;
    overflow: hidden;
    padding-left: 4% !important;
    padding-right: 4% !important;
    width: auto !important;
}


#content {
    float: left !important;
    width: 57.2% !important;
    padding: 0.4% !important;
}

#mood {
    float: right !important;
}

#mood a.up{color:green !important;}
#mood a.down{color:red !important;}

.sidebar_left{
    width: 16% !important;
    clear: left;
}

.sidebar_right {
    float: left;
    min-height: 200px;
    width: 25% !important;
}

a{
    color: LightSteelBlue!important;
    font-weight: normal !important;
    font-family: Verdana,sans-serif !important;
}

a.user{
    color: darkblue !important;
}
h1,h2,h3,h4{
    color: slategray !important;
    font-weight: normal !important;
    font-family: Verdana,sans-serif !important;
}

h1 span{
    color: slategray !important;
    font-weight: normal !important;
    font-family: Verdana,sans-serif !important;
}

p,span{
    font-weight: normal !important;
    font-family: Verdana,sans-serif !important;

}

h4, .reklamodateli p, #mainthemes p{
    color: slategray !important;
    font-size: 20px !important;
    line-height: 1em !important;
    font-weight: normal !important;
    text-align: left !important;
    margin-bottom: 10px !important;
    
}

#mainthemes li:before{
}


table.trade {
    background-color: white !important;
}

#nav {
    background: none repeat scroll 0 0 slategray !important;
    border: 1px solid gray !important;
    float: right;
    height: 26px;
    padding: 2px 10px 0 0 !important;
    width: 898px !important;
}

#wrapper div.active{
    width: 910px !important;
    margin-right: 0 !important;
}

#wrapper div.item {
    background-color: whitesmoke !important;
    width: 40% !important;
}

.tagb {
    background: none repeat scroll 0 0 white !important;
    margin: 2em auto 0 !important;
    position: relative;
    width: 60% !important;
}

.active div{
    border: medium none !important;
}

#pagination {
    margin: 0 auto !important;
    padding: 0 !important;
    width: 300px !important;
}

.white-back {
    background: none repeat scroll 0 0 white !important;
}

#wrapper div.desc {
    margin: 0 !important;
    padding: 0 !important;
    width: 60% !important;
    background-color: whitesmoke !important;
    height: 100% !important;
}

#search {
    background-color: white !important;
}

.menu li a{
    text-decoration: underline !important;
}

.reklamodateli{
    font-size: 0.7em !important;
}

.bbr, .block{
    background: none repeat scroll 0 0 #F0F0E7 !important;
    border-radius: 5px 5px 5px 5px;
    border: medium none !important;
    overflow: hidden;
    height: auto !important;
    margin-top: 9px !important;
    margin-left: 0 !important;
    margin-right: 0 !important;

}
.sme div{
padding-bottom: 1em !important;
}
.trt div{
    padding-bottom: 1em !important;
}

.trt .c{

}
div.sme span.d{
    color: darkblue !important;
    }
div.sme div:first-child span {
    color: red !important;
}

.block.blogs ul.list {
    text-align: left !important;
}

.block.blogs ul.list li {
    list-style: none outside none !important;
}



}
