css:
body 
{
    background: url("http://i.imgur.com/nxED1.png") #96fedb;
    background-repeat:no-repeat;
    background-position:right bottom;
    background-attachment:fixed;
    color: black;
    font-family: arial,helvetica,sans-serif;
    font-size: 10pt;
    margin: 0 8px;
    padding-left: 5px;
    padding-right: 5px;
}



a.bodylink
{
    color: #2f634f;
}
a, a:visited 
{
    color: #2f634f;
    text-decoration: underline;
}
a:hover, p.intro a.post_no:hover 
{
    color: #2f634f;
}
a.post_no 
{
    margin: 1;
    padding: 1;
    text-decoration: none;
}
p.intro a.post_no 
{
    color: inherit;
}

p.intro a.post_no, p.intro a.email 
{
    margin: 0;
}

p.intro a.email span.name 
{
    color: #2f634f;
}

p.intro a.email:hover span.name 
{
    color: #2f634f;
}

p.intro label 
{
    display: inline;
}



h2 
{
    font-size: 11pt;
    margin: 0;
    padding: 0;
    color: #489d7c;
}
header 
{
    margin: 1em 0;
}
h1 
{
    font-family: arial;
    font-size: 35pt;
    letter-spacing: -2px;
    margin: 0;
}
header div.subtitle, h1 
{
    color: #2f634f;
    text-align: center;
}
header div.subtitle 
{
    font-size: 8pt;
}



form[name="post"] 
{
    margin-bottom: 4em;
}
form table 
{
    margin: auto;
}
form table input 
{
    height: auto;
}
input[type="submit"]
{
    padding: 2px;
}
table.mod.config-editor 
{
    font-size: 9pt;
    width: 100%;
}

table.mod.config-editor td 
{
    border-bottom: 1px solid #489d7c;
    padding: 5px;
    text-align: left;
}

table.mod.config-editor input[type="text"] 
{
    width: 98%;
}
input[value="New Topic"]
{
	margin-bottom: -1px;
	padding: 1px;
	margin-top: -0px;
	height: 27px;
}
input[type="text"], input[type="password"], textarea 
{
    border: 1px solid #ccc;
    padding: 4px;
	text-indent: 0;
    text-shadow: none;
    text-transform: none;
    word-spacing: normal;
	font-family: arial;
}
form table tr td 
{
    margin: 0;
    padding: 0;
    text-align: left;
}
table.modlog 
{
    margin: auto;
    width: 100%;
}




table.modlog tr td 
{
    margin: 0;
    padding: 4px 15px 0 0;
    text-align: left;
}




table.modlog tr th 
{
    padding: 4px 15px 5px 5px;
    text-align: left;
    white-space: nowrap;
}




table.modlog tr th 
{
    background: none repeat scroll 0 0 #F5F5F5;
}




td.minimal, th.minimal 
{
    white-space: nowrap;
    width: 1%;
}
form table.mod tr td 
{
    padding: 2px;
}

form table tr th 
{
    padding: 4px;
    text-align: left;
}
form table tr th 
{
    background: none repeat scroll 0 0 #aceae5;
    border-color: #489d7c;
    border-style: solid solid solid solid;
    border-width: 1px;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 4px;
    color: #489d7c;	
}
form table tr td div 
{
    float: left;
    padding-left: 3px;
    text-align: center;
}

form table tr td div input 
{
    display: block;
    margin: 2px auto 0;
}
input.delete 
{
    float: left;
    margin: 1px 6px 0 0;
}
form table tr td div label 
{
    font-size: 10px;
}



.unimportant, .unimportant * 
{
    font-size: 10px;
}

p.fileinfo 
{
    display: block;
    margin: 0;
    padding-right: 7em;
}



div.post img 
{
    margin: 5px 20px 0 0;
    padding: 5px;
}
div.delete 
{
    float: right;
}

div.post.reply p 
{
    margin: 0.3em 0 0;
}
div.boardlist 
{
    color: #489d7c;
    font-size: 12pt;
    margin-top: 3px;
}

div.boardlist.bottom 
{
    margin-top: 20px;
}

div.boardlist a 
{
    text-decoration: none;
}

div.report 
{
    color: #333333;
}



div.top_notice 
{
    margin: 5px auto;
    text-align: center;
}
div.post.reply p.body 
{
    margin-left: 1.8em;
    margin-top: 0.8em;
    padding-bottom: 0.3em;
    padding-right: 3em;
}

div.post.reply.highlighted 
{
    background: #fdfffc;
    border-color: #84f1eb;
    border-style: solid solid solid solid;
    border-width: 3px;
    display: inline-block;
    margin: 0.8em 16px;
    padding: 0.4em 0.5em 0.7em 0.8em;
    border-top-left-radius: 12px;
    border-top-right-radius: 12px;
    border-bottom-right-radius: 12px;
    border-bottom-left-radius: 12px;
}

div.post.reply p.body a 
{
    color: #489d7c;
}

div.post 
{
    max-width: 75%;
}

div.post p.body 
{
    white-space: pre-wrap;
    word-wrap: break-word;
}

div.post.reply 
{
    background: #aceae5;
    border-color: #50cbc9;
    border-style: solid solid solid solid;
    border-width: 2px;
    display: inline-block;
    margin: 0.8em 16px;
    padding: 0.4em 0.5em 0.7em 0.8em;
    border-top-left-radius: 12px;
    border-top-right-radius: 12px;
    border-bottom-right-radius: 12px;
    border-bottom-left-radius: 12px;
}
div.post img.icon 
{
    display: inline;
    float: none;
    margin: 0 5px;
    padding: 0;
}

div.post.op 
{
    margin-bottom: 5px;
    margin-right: 20px;
}

div.post.op hr 
{
    border-color: #489d7c;
}
div.banner 
{
    Background: #96fedb;
    color: black;
    font-size: 12pt;
    font-weight: bold;
    margin: 1em 0;
    text-align: center;
}

div.banner, div.banner a 
{
    color: black;
}

div.banner a:hover 
{
    color: #489d7c;
    text-decoration: none;
}



img.banner 
{
    border: 1px solid #489d7c;
    float: none;
    margin: 12px auto 0;
}

img 
{
    border: medium none;
    display: block;
    float: left;
    margin: 10px 20px;
}


p.intro 
{
    margin: 0.5em 0;
    padding: 0 0 0.2em;
}



p.intro span.subject 
{
    color: #2f634f;
    font-weight: bold;
    font-size: 14px;
}

p.intro span.name 
{
    color: #2f634f;
    font-weight: bold;
    font-size: 13px;
}

p.intro span.capcode, p.intro a.capcode, p.intro a.nametag 
{
    color: #2f634f;
    font-weight: bold;
    margin-left: 0;
}

p.intro a 
{
    margin-left: 8px;
}



span.trip 
{
    color: #489d7c;
}

span.quote 
{
    color: #489d7c;
}

span.omitted 
{
    display: block;
    margin-top: 1em;
}

br.clear 
{
    clear: left;
    display: block;
}

span.controls 
{
    float: right;
    font-size: 80%;
    margin: 0;
    padding: 0;
}

span.controls.op 
{
    float: none;
    margin-left: 10px;
}

span.controls a 
{
    margin: 0;
}

div#wrap 
{
    margin: 0 auto;
    width: 900px;
}

div.ban 
{
    border-top-left-radius: 12px;
    border-top-right-radius: 12px;
    border-bottom-right-radius: 12px;
    border-bottom-left-radius: 12px;
    background: none repeat scroll 0 0 white;
    border: 1px solid #489d7c;
    margin: 30px auto;
    max-width: 700px;
}

div.ban p, div.ban h2 
{
    padding: 3px 7px;
}

div.ban h2 
{
    background: none repeat scroll 0 0 #FAFAFA;
    color: black;
    font-size: 12pt;
}

div.ban p 
{
    font-size: 12px;
    margin-bottom: 12px;
}

div.ban p.reason 
{
    font-weight: bold;
}

span.heading 
{
    color: #489d7c;
    display: block;
    font-size: 11pt;
    font-weight: bold;
}

span.spoiler 
{
    background: none repeat scroll 0 0 black;
    color: black;
}

div.post.reply p.body span.spoiler a 
{
    color: black;
}

span.spoiler:hover, div.post.reply p.body span.spoiler:hover a 
{
    color: white;
}

div.styles 
{
    float: right;
    padding-bottom: 20px;
}

div.styles a 
{
    margin: 0 10px;
}

div.styles a.selected 
{
    text-decoration: none;
}

table.test 
{
    width: 100%;
}

table.test td, table.test th 
{
    padding: 5px;
    text-align: left;
}

table.test tr.h th 
{
    background: none repeat scroll 0 0 #aaaaaa;
}

table.test td img 
{
    margin: 0;
}

fieldset label 
{
    display: block;
}

div.pages 
{
    background: #FEFEFE;
    color: #FF086B;
    margin-left: 17px;
    margin-top: 10px;
    border-color: #D13B76;
    border-style: solid solid solid solid;
    border-width: 2px;
    margin: 0.8em 16px;
    padding: 0.4em 0.5em 0.7em 0.8em;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 4px;
}

div.pages a.selected 
{
    color: black;
    font-weight: bolder;
}

div.pages a 
{
    text-decoration: none;
}

div.pages form 
{
    display: inline;
    margin: 0;
    padding: 0;
}

div.pages form input 
{
    display: inline;
    margin: 0 5px;
}

hr 
{
    border-color: #489d7c;
    border-right: medium none;
    border-style: solid none none;
    border-width: 1px medium medium;
    clear: left;
    height: 0;
}



span.public_ban 
{
    color: red;
    display: block;
    font-weight: bold;
    margin-top: 15px;
}

span.toolong 
{
    display: block;
    margin-top: 15px;
}

div.blotter 
{
    color: #489d7c;
    font-weight: bold;
    text-align: center;
}



.homepage header h1 
{
    font-size: 40pt;
}

.homepage header .subtitle 
{
    font-size: 16pt;
}

.homeblock 
{
    background: #fdfffc;
    border: 1px solid #2f634f;
    border-radius: 3px 3px 3px 3px;
    font-size: 110%;
    margin: 1em auto;
    min-width: 300px;
    padding: 1em;
    width: 75%;
    border-top-left-radius: 30px;
    border-top-right-radius: 8px;
    border-bottom-right-radius: 30px;
    border-bottom-left-radius: 8px;
}

.mainhomeblock 
{
    font-size: 140%;
}

.hashtag 
{
    background: #fdfffc;
    border-color: #80f3ec;
    border-style: none solid solid none;
    border-width: 1px;
    box-shadow: 1px 1px 3px #222222;
    color: #489d7c;
    display: inline-block;
    margin-right: 0.5em;
    padding: 0.2em 0.5em;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 4px;
}
