css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.linda-goodman.com")

/* Autor: 7thGuardian */
/* Mail: xsallin@gmail.com */ 

{

body 

{
background: linear-gradient(to bottom,#B2E1FF 0%,#66B6FC 50%,#B2E1FF 100%)!important; 
color:#111 !important;
text-align: center !important;
}


table 

{
margin-right: auto !important;
margin-left: auto !important;
border:#B2E1FF  1.7px solid !important;
border-radius: 0.25em;
}

table, tr, td, table.tableThumb 

{
background: linear-gradient(to bottom,#B2E1FF 0%,#66B6FC 200%)!important; 
}

font {color:#111 !important;text-decoration:none !important;}




/*Links*/



a:link

{color:#0011FF !important;text-decoration:none !important;}
 


a:visited, body_avisited

{color:#560098 !important;text-decoration:none !important;}



a:hover, a:active, body_ahover

{color:#211922 !important;text-decoration:underline !important;}




/*Quotes*/


BLOCKQUOTE, WBR 

{
border:#CCE6FF 15px solid !important;
border-radius: 1em;
background: #CCE6FF !important;
box-shadow: 1px 3px 5px 3px #333;
}

}
