css:
#home_button.tab.iconic > a,
#home_button.tab.iconic.selected > a {
  background-image: url("http://media.tumblr.com/53d85936cefe9803102cd050e61a125b/tumblr_inline_mf8vcxevGS1r29wxr.png") !important;
  background-size: 37px 37px !important;
  background-repeat: no-repeat !important;
  opacity: .65 !important;
}
#home_button.tab.iconic > a:hover,{
  opacity: .85 !important;
  -moz-transition: ease-out all .25s !important;
}
#home_button.tab.iconic.selected > a {
  opacity: 1 !important;
  -moz-transition: ease-out all .25s !important;
}
