css:
/*PlanetInfowars RexMundi412 Dark Version 1.04 Created by RexMundi412 on 12/18/2012, latest update on 1/16/2013*/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("planet.infowars.com") {


/*Base*/

p
{color:#999!important; font-size:16px!important;}

a:hover, a:active
{color:#00C3FF!important; background:#333!important; box-shadow: 0px 0px 7px rgba(31, 179, 221, 0.7)!important;}

div.generic-button a
{color:#fff!important; background: linear-gradient(rgba(200, 200, 200, 0.5), rgba(0, 0, 0, 0)) repeat scroll 0% 0% rgb(5, 5, 5)!important; border: 1px solid #444!important;}

button, a.button, input
{color:#fff!important; background: linear-gradient(rgba(200, 200, 200, 0.5), rgba(0, 0, 0, 0)) repeat scroll 0% 0% rgb(5, 5, 5)!important; border: 1px solid #444!important;}

button:hover, a.button:hover
{color:#00C3FF!important; box-shadow: 0px 0px 7px rgba(31, 179, 221, 0.7)!important;}

h2 a
{color:#fff!important; text-shadow: -1px 0 #00C3FF, 0 1px #00C3FF, 1px 0 #00C3FF, 0 -1px #00C3FF!important; background: linear-gradient(rgba(200, 200, 200, 0.5), rgba(0, 0, 0, 0)) repeat scroll 0% 0% rgb(5, 5, 5)!important; border: 1px solid #444!important;}

#search-bar input[type="text"]
{background:#000!important;}

#search-bar input[type="submit"] 
{color:#fff!important; background: linear-gradient(rgba(200, 200, 200, 0.5), rgba(0, 0, 0, 0)) repeat scroll 0% 0% rgb(5, 5, 5)!important; border: 1px solid #444!important;}

#search-bar input:hover[type="submit"]
{color:#00C3FF!important; box-shadow: 0px 0px 7px rgba(31, 179, 221, 0.7)!important;}

#header_wrapper
{background:#000!important;}

#header 
{background:#111!important;}

ul#nav.menu a
{background:#222!important; color:#999!important; font-size:15px!important;}

ul#nav.menu a:hover, a:active
{background:#333!important; color:#00C3FF!important; font-size:15px!important; box-shadow: 0px 0px 7px rgba(31, 179, 221, 0.7)!important;}

strong
{color:#777!important;}

ul#menu-categories-menu li.menu-item a
{color:#888!important;border: 1px solid rgb(0, 150, 200)!important;}

ul#menu-categories-menu li.menu-item a:hover
{color:#00C3FF!important;  box-shadow: 0px 0px 7px rgba(31, 179, 221, 0.7)!important;}

#control_panel_logged
{background:#000!important;}

#show_activate
{background:#222!important;}

div#content .padder 
{background:#000!important; border: 1px solid #333!important;}

div#item-header div#item-header-content
{background:#111!important;}

div#container
{background:#111!important;}



/*Home*/

li.tab_title a, li.tab_title2 a
{background:#111!important;}

.groups_front_page_holder 
{background:#111!important;}

.blocks_container ul li a
{color:#888!important;}

.blocks_container h2 a
{text-shadow:none!important}

li.exterior_li
{background:#111!important;}

input#groups_search
{color:#111!important}

input#zip
{color:#111!important}

.item-title-front-page a
{font-size:14px!important;}



/*Members*/

#latest-update
{color:#888!important;}

#latest-update a
{color:#999!important;}


/*Members-Activity*/

div.item-list-tabs
{background:#111!important;}


/*Start Groups*/

.standard-form textarea 
{color:#FFF!important; background:#111!important; border: 1px solid #333!important;}

.standard-form div.submit input:hover
{color:#00C3FF!important; background:#333!important; box-shadow: 0px 0px 7px rgba(31, 179, 221, 0.7)!important;}


/*Category*/

div.author-box 
{background:#111!important;}


/*Members*/

table tr.alt td 
{background:#111!important;}

div.pagination
{background:#222!important;}


/*Members-Messages*/

div.messages-options-nav 
{background:#222!important;}


/*Members-Messages-View*/

div#message-thread div.message-content 
{background:#111!important;}

div#message-thread div.alt 
{background:#222!important;}

table#message-threads tr.unread td 
{color:#999!important; background:#333!important;}


/*Members-Messages-Compose*/

.standard-form textarea#message_content 
{color:#fff!important; background:#111!important;}


/*Members-Friends-Requests*/

span.activity, div#message p
{background:#111!important; border: 1px solid #333!important;}


/*Members-Activity-Permalink*/

body.activity-permalink .activity-list li .activity-content 
{background:#111!important; border: 1px solid #333!important;}

body.activity-permalink .activity-list li .activity-header > p
{background:none!important;}




/*Groups*/

#item-meta p a
{color:#00ABD1!important;}

#item-meta p a:hover
{color:#fff!important; background:#333!important;}

a.bp-primary-action span, #reply-title small a span 
{background:#111!important; border: 1px solid #333!important; border-top:none!important; text-shadow: 1px 1px #000!important;}

a.group-button.leave-group:hover, a.leave-group:hover
{color:#00ABD1!important; background:#333!important;}

#whats-new
{background:#111!important; border: 1px solid rgb(94, 218, 255)!important;}

form#whats-new-form textarea
{color:#fff!important;}

div#item-header 
{background:#111!important;}

div#object-nav.item-list-tabs 
{padding-top:5px!important; background:#111!important; border: 1px solid #555!important; border-bottom:none!important;}

div.item-list-tabs ul li a, div.item-list-tabs ul li span
{border: 1px solid #333!important;}

div.item-list-tabs ul li.selected a, div.item-list-tabs ul li.current a
{color:#fff!important; background:#333!important; border: 1px solid #00ABD1!important; border-bottom:none!important;}

div#subnav.item-list-tabs 
{background:#111!important}

div.item-list-tabs ul li a span
{Color:#00ABD1!important; background:#111!important; border: 1px solid #00ABD1!important;}

div.item-list-tabs ul li.current a span
{Color:#fff!important; background:#222!important; border: 1px solid #999!important;}

p:last-child
{font-size:14px!important;}

input:hover[type="submit"], input:hover[type="button"]
{color:#00C3FF!important;  box-shadow: 0px 0px 7px rgba(31, 179, 221, 0.7)!important;}

.acomment-reply
{background:#222!important; border: 1px solid #444!important;}

.acomment-options a
{background:#222!important; border: 1px solid #444!important;}

div.activity-comments ul 
{background:#222!important}

ul #activity-stream, ul#activity-stream li
{background:#111!important; border-top: 2px solid #222!important;}

div.activity-comments form.root, div.activity-comments li form.ac-form 
{background:#111!important; border: 2px solid #333!important;}

div.activity-comments form .ac-textarea
{background:#222!important; border: 1px solid #555!important; box-shadow: 0px 0px 7px rgba(31, 179, 221, 0.7)!important;}

div.activity-comments form textarea 
{background:#000!important; color:#fff!important; font-size:14px!important;}

.activity-list li.load-more a 
{color:#fff!important;}

div.activity-header p
{font-size:14px!important;}

div.acomment-meta
{font-size:12px!important;}

.activity-inner > p, p:last-child 
{font-size:16px!important;}


/*Groups-Missions*/

#add-event-form
{background:#222!important;}

input#save:hover
{color:#00C3FF!important; background:#333!important; box-shadow: 0px 0px 7px rgba(31, 179, 221, 0.7)!important;}

table.calendar a
{color:#555!important;}

.event_info
{color:#888!important; background: linear-gradient(rgba(20, 20, 20, 0.5), rgba(200, 200, 200, 0)) repeat scroll 0% 0% rgb(1, 1, 1)!important; border: 1px solid #333!important;}

.assignment-yet 
{background:#333!important;}

a.NNevent_title.rsvp-att-link:hover
{color#fff!important;}

/*Groups-Missions-AreYouTakingTheMission*/

.wpcf7
{background:#222!important;}

.wpcf7-form 
{color:#999!important;}

input#guests
{color:#111!important;}


/*Groups-Send-Invites*/

div#invite-list
{color:#999!important; background:#111!important;}


/*Groups-Search-Members*/

input#city, input#search_state, input#display_name
{color:#111!important;}

.search_members_div
{background:#333!important;}



/*Forums*/

th#th-title, table.forum th#th-postcount, table.forum th#th-freshness
{color:#888!important; background:#333!important;}



/*Avatars*/

img.avatar
{border: 1px solid #00ABD1!important;}

img.avatar:hover
{border: 1px solid #fff!important; box-shadow: 0px 0px 7px rgba(31, 179, 221, 0.7)!important;}



/*Sidebar*/

h3.widgettitle
{color:#888!important;}

h3.widgettitle a.rsswidget
{color:#00C3FF!important;}

.widget_search input[type="text"]
{color:#999!important;}



/*Contact Us*/

#c_form
{background:#111!important; border: 1px solid #333!important;}

#c_form input
{color:#111!important;}



}
