css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pspdemocenter.org") {



/* ============== Overall ============== */

html
	{
	padding:0 !important;
	}

body
	{
	font:12px Verdana, Arial, sans-serif !important;
background-color:#000000;
	background-image:url('data:image/png;base64,') !important;
background-repeat: repeat-x !important;
	margin:15px !important;
	padding:0 !important;
	color:#FFF !important;
	}

a 			{ text-decoration:none !important; border-width:0 !important; color:#556699 !important; }
a:hover 	{ text-decoration:underline !important; }

h1 	{ font-size:250% !important; letter-spacing: -2px !important; font-weight:normal !important;}
h2 	{ font-size:170% !important; letter-spacing: -2px !important; font-weight:normal !important; }
h3 	{ font-size:140% !important; margin:1em 1em 1em 0 !important; letter-spacing: -1px !important; font-weight:normal !important; }
h4 	{ font-size:100% !important; font-weight:bold !important; margin:1.5em 1em 1em 0 !important; }
h5 	{ font-size:95% !important; margin:1em 1em 1em 0 !important; }
h6 	{ font-size:90% !important; margin:1em 1em 1em 0 !important; }

.bbstyle1	{ font-size:160% !important; font-weight:bold !important; }
.bbstyle2	{ font-size:130% !important; font-weight:bold !important; }
.bbstyle3	{ font-size:120% !important; font-weight:bold !important; }
.bbstyle4	{ font-size:110% !important; font-weight:bold !important; }
.bbstyle5	{  }
.bbstyle6	{  }
.bbstyle7	{  }
.bbstyle8	{  }
.bbstyle9	{  }

#container
	{
	padding:0 !important;
	margin:0 !important;
	width:900px !important;
	background-color:#000000 !important;
	border:1px solid #000000 !important;
	background-image:none !important;
	}

#header
	{
	padding:0 !important;
	margin:0 !important;
	height:70px !important;
	}

#nav
	{
	font-size:110% !important;
	margin:0 0 0 0 !important;
	padding:3px !important;
	text-align:center !important;
	background-color:#888 !important;
	letter-spacing:2px !important;
	color:#CCCCCC !important;
	background-image:url('data:image/png;base64,') !important;
   	}

#nav a
	{
	color:#FFF !important;
   	}

#nav a:hover
	{
	text-decoration:none !important;
	}

#nav ul
	{
	padding:2px !important;
    	margin:0 !important;
	}

#nav li
	{
	display:inline !important;
	list-style:none !important;
	padding:2px 6px 2px 6px !important;
	margin:0 !important;
	}

#user
	{
	margin:0 !important;
	padding:3px !important;
	color:#FFF !important;
	text-align:right !important;
	background-color:Transparent !important;
	font-size:95% !important;
	background-image:none !important;
	}

#user ul
	{
	padding:2px !important;
	margin:0 !important;
	}

#user li
	{
	display:inline !important;
	list-style:none !important;
	margin:0 !important;
	padding:3px !important;
	}

#banner
	{
	margin:0 0 0 0 !important;
	padding:15px 10px 2px 10px !important;
	text-align:center !important;
	letter-spacing:2px !important;
   	}

#title
	{
	margin:20px 20px 12px 20px !important;
	font-size:170% !important;
	letter-spacing: -2px !important;
	}

#subtitle
	{
	margin:4px 20px 16px 20px !important;
	vertical-align:top !important;
	color:#999999 !important;
	}

#adminmenu
	{
	margin:0 20px 2px 20px !important;
	}

#main
	{
	padding:16px !important;
	margin:0 !important;
	vertical-align:top !important;
	color:#999999 !important;
	}

#image
	{
	padding:8px !important;
	margin:0 !important;
	vertical-align:top !important;
	color:#999999 !important;
	}

#popup
	{
	padding:16px !important;
	margin:0 !important;
	vertical-align:top !important;
	color:#999999 !important;
	}

#side
	{
	border:4px solid #FFFFFF !important;
	background-color:#F5F5F5 !important;
	padding:8px !important;
	font-size:90% !important;
	}

#footer
	{
	clear:both !important;
	padding:8px !important;
	margin:0 !important;
	font:80% Verdana, Arial, sans-serif !important;
	color:#888888 !important;
	text-align:center !important;
	background-image:none !important;

	}

/* ============== Various ============== */

.disp		{ padding:12px !important; margin:0 !important; }
.block		{ padding:8px !important; margin:5px !important;	border:1px solid #EAEAEA !important; }
.vbanner		{ padding:0px !important; margin:5px !important;	}
.block h4	{ margin-top:0 !important; }
.error		{ padding:24px 8px 24px 8px !important; margin:0 !important; color:#CC1212 !important; font-weight:bold !important; font-size:110% !important; text-align:center !important; }
.codetitle	{ font-size:10px !important; color:#59739B !important; padding:2px !important; margin-top:5px !important; }
.code 		{ font-size:11px !important; color:#59739B !important; background-color:#EFF0C2 !important; padding:2px !important; margin-bottom:5px !important; font-family:monospace !important; }
.valid 		{ height:48px !important; text-align:center !important; vertical-align:middle !important; }
.download 	{ font-size:110% !important; text-align:center !important; background-color: #333333 !important; padding:16px !important; margin-top:16px !important; }
.paging		{ text-align:right !important; padding:12px !important; font-size:90% !important; }
.coltop		{ text-align:center !important; font-size:95% !important; color:#888888 !important; }
.centerall 	{ text-align:center !important; }
.centercenterall 	{ text-align:center !important; vertical-align:middle !important; }
.vcenter	{ vertical-align:middle !important; }
.desc 		{ font-size:95% !important; padding:3px !important; color:#646464 !important; }
li 			{ margin:2px !important; }

img			{ border-width:0 !important; }
ul 			{ list-style-type:square !important; }
form		{ margin:0 !important; color:#888888 !important; }
.colleft	{ float:left !important; margin:0px 4px 0px 4px !important; }
.colright	{ float:right !important; margin:0px 4px 0px 4px !important; }
.bar_front 	{ background-color:#92CC5E !important; border-right:0px solid #333333 !important; padding:0 !important; color:#DDDDDD !important; height:8px !important; }
.bar_back 	{ background-color:#EBECF1 !important; border:1px solid #333333 !important; margin:0 7px 0 7px !important; height:8px !important; }
.bbcodes a img { border:1px outset #A0A0A0 !important; background-color:#999BAC !important; }
.smilies a img { border:0 !important; background-color:transparent !important; }
.today		{ border:1px dashed #DDAA00 !important; }
.signature 		{ color:#979797 !important; font-size:80% !important; max-height:96px !important; overflow:auto !important; }
.option	{ background-color:#333333 !important; color:#888888 !important; border:1px solid #333333 !important; }
select { background-color:#333333 !important; color:#888888 !important; border:1px solid #999999 !important; }
input { background-color:#333333 !important; color:#888888 !important; border:1px solid #999999 !important; }
textarea { background-color:transparent !important; color:#888888 !important; border:1px solid #999999 !important; }


/* ============== Tables ============== */

table td 			{ vertical-align:top !important; }
table.flat		{ width:99% !important; margin:0 !important; padding:0 !important; border-collapse:collapse !important; }
table.flat td 	{ padding:0 !important; margin:0 !important}
table.main 		{ width:99% !important; margin:0 !important; padding:4px !important; }
table.main td 	{ padding:4px !important; }
table.cells 	{ width:99% !important; margin:0 !important; padding:0 !important; border:2px solid #CCCCCC !important; }
table.cells td	{ padding:2px !important; margin:0 !important; border:1px solid #333333 !important; }
table.list 		{ width:99% !important; margin:0 !important; padding:5px !important; }
table.list td 	{ padding:12px !important; }






}
