css:
@-moz-document domain('2ch.hk') {

#adminbar { display: none; }
#title    { display: none; }
.abupost  { display: none; }
.footer   { display: none; }
.mobile   { display: none; }
hr        { display: none; }
.filetitle { font-weight: normal; font-size: 1.1em; }

a { color: #4271ae; }
.orange  { color: #4271ae; }

td a {
  color: #4271ae; 
  font-size: 1.2em !important;
  background: #fafafa;
  padding: 3px 7px 3px 7px;
  border-radius: 7px; 
}

.rmenu a { 
  color: #4271ae; 
  font-size: 1.2em !important;
  background: #fafafa;
  padding: 3px 7px 3px 7px;
  border-radius: 7px; 
}
.rmenu a:visited { color: #f5871f; }

html, body {
  font-size: 1.1em !important;
  font-family: Ubuntu, "Helvetica Neue", Helvetica, Arial, Sans-Serif !important ;
  line-height: 25px;

  background-color: #FFF;
}

.reply {
  background: #fafafa;
  border: solid 1px #d6d6d6;
  padding: 0;
  margin: 0;
}

tt, code, kbd, samp {
  font-family: Inconsolata;
  font-size: 1.2em;
}

}
