@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trello.com") {

  .list-card:hover .card-short-id {
    display: block !important;
    position: absolute;
    right:2px;
    bottom:0px;
    color: #9C9C9C;
    cursor: default;    
    font-size: 11px;
    line-height: 16px;
    height: 16px;
    text-decoration: none;
  }


/* end */
}
