css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.linda-goodman.com")

/* Autor: 7thGuardian */
/* Mail: xsallin@gmail.com */ 

{

body 

{
background: linear-gradient(to bottom,#AFEEEE 0%,#40E0D0 50%,#AFEEEE 100%)!important; 
color:#111 !important;
text-align: center !important;
}


table 

{
margin-right: auto !important;
margin-left: auto !important;
border:#AFEEEE  1.7px solid !important;
border-radius: 0.25em;
}

table, tr, td, table.tableThumb 

{
background: linear-gradient(to bottom,#AFEEEE 0%,#40E0D0 200%)!important; 
}

font {color:#111 !important;text-decoration:none !important;}




/*Links*/



a:link

{color:#0400E6 !important;text-decoration:none !important;}
 


a:visited, body_avisited

{color:#7100DB !important;text-decoration:none !important;}



a:hover, a:active, body_ahover

{color:#211922 !important;text-decoration:underline !important;}




/*Quotes*/


BLOCKQUOTE, WBR 

{
border:#D9F7F7 15px solid !important;
border-radius: 1em;
background: #D9F7F7 !important;
box-shadow: 1px 3px 5px 3px #555;
}

}
