css:
@-moz-document domain("www.pandora.com") {
html {
overflow-y: auto !important;
}

body {
background-color: #000 !important;
}

#brandingBar {
background: #1b1b1b; /* Old browsers */
background: -moz-linear-gradient(bottom,  #1b1b1b 65%, #555555 100%) !important; /* FF3.6+ */
background: -webkit-linear-gradient(bottom,  #1b1b1b 65%,#555555 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(bottom,  #1b1b1b 65%,#555555 100%) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(bottom,  #1b1b1b 65%,#555555 100%) !important; /* IE10+ */
background: linear-gradient(to top,  #1b1b1b 65%,#555555 100%) !important; /* W3C */
border-bottom: 1px solid #aaa !important;
}

#brandingBar .middlecolumn {
color: #FFF !important;
}

#brandingBar .rightcolumn {
color: #FFF !important;
}

#brandingBar .rightcolumn a {
color: #FFF !important;
}

#brandingBar .rightcolumn .user_activator:hover {
background: rgba(255,255,255,0.1) !important;
}

.stationChangeSelectorNoMenu p {
width: auto !important;
max-width: none !important;
}

#playerBar {
background: #1b1b1b; /* Old browsers */
background: -moz-linear-gradient(top,  #1b1b1b 65%, #555555 100%) !important; /* FF3.6+ */
background: -webkit-linear-gradient(top,  #1b1b1b 65%,#555555 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  #1b1b1b 65%,#555555 100%) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  #1b1b1b 65%,#555555 100%) !important; /* IE10+ */
background: linear-gradient(to bottom,  #1b1b1b 65%,#555555 100%) !important; /* W3C */
}

#playerBar .searchBox {
margin-left: 18px !important;
background-image: none !important;
padding: 0 !important;
}

#playerBar .searchBox:before {
content: "New Station" !important;
color: #EEE !important;
font-weight: bold !important;
position: relative !important;
top: 6px !important;
left: 5px !important;
padding: 2px 5px 3px 6px !important;
border-top-left-radius: 3px !important;
border-bottom-left-radius: 3px !important;


background: #3b3b3b; /* Old browsers */
background: -moz-linear-gradient(top,  #3b3b3b, #555555) !important; /* FF3.6+ */
background: -webkit-linear-gradient(top,  #3b3b3b,#555555) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  #3b3b3b, #555555) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  #3b3b3b , #555555) !important; /* IE10+ */
background: linear-gradient(to bottom,  #3b3b3b , #555555) !important; /* W3C */
}

#playerBar .searchInput {
width: 100% !important;
background-color: #FFF !important;
margin-left: 2px !important;
padding: 3px 5px 3px 5px !important;
border-bottom-right-radius: 2px !important;
border-top-right-radius: 2px !important;
}

#searchPopupNavPosition {
left: 80px !important;
}

#mainContainer {
background: #7b7b7b; /* Old browsers */
background: -moz-radial-gradient(center, ellipse cover,  #7b7b7b 30%, #000000 70%) !important; /* FF3.6+ */
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(30%,#7b7b7b), color-stop(70%,#000000)) !important; /* Chrome,Safari4+ */
background: -webkit-radial-gradient(center, ellipse cover,  #7b7b7b 30%,#000000 70%) !important; /* Chrome10+,Safari5.1+ */
background: -o-radial-gradient(center, ellipse cover,  #7b7b7b 30%,#000000 70%) !important; /* Opera 12+ */
background: -ms-radial-gradient(center, ellipse cover,  #7b7b7b 30%,#000000 70%) !important; /* IE10+ */
background: radial-gradient(ellipse at center,  #7b7b7b 30%,#000000 70%) !important; /* W3C */
}

.adSupported-layout #adLayout {
min-width: 666px !important;
width: 70% !important;
}

#mainContentContainer {
width: 100% !important;
background: #626262 !important;
}

.contentContainer .top {
background-color: #2b2b2b !important;
border-top: 1px solid #444 !important;
}

.contentnav {
background-color: #2b2b2b !important;
border: 0 !important;
}

.stationContent {
background-color: #4b4b4b !important;
border-right: 1px solid #CCC !important;
}

.stationListItem .stationName {
color: #FFF !important;
text-shadow: 1px 1px 0 #333 !important;
}

.stationListItem .stationName_hover {
background-color: #777 !important;
}

.stationListItem.selected {
border-top: 1px solid transparent !important;
border-bottom: 1px solid transparent !important;

background: #888888; /* Old browsers */
background: -moz-radial-gradient(center, ellipse cover,  #888888 0%, #333333 100%) !important; /* FF3.6+ */
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,#888888), color-stop(100%,#333333)) !important; /* Chrome,Safari4+ */
background: -webkit-radial-gradient(center, ellipse cover,  #888888 0%,#333333 100%)  !important; /* Chrome10+,Safari5.1+ */
background: -o-radial-gradient(center, ellipse cover,  #888888 0%,#333333 100%) !important; /* Opera 12+ */
background: -ms-radial-gradient(center, ellipse cover,  #888888 0%,#333333 100%) !important; /* IE10+ */
background: radial-gradient(ellipse at center,  #888888 0%,#333333 100%) !important; /* W3C */
}

.stationListItem.selected .options {
background: #CCC !important;
}

.btn_bg_sort {
color: #FFF !important;
text-shadow: 1px 1px 0 #333 !important;

background: #7b7b7b; /* Old browsers */
background: -moz-radial-gradient(center, ellipse cover,  #7b7b7b 0%, #5b5b5b 100%) !important; /* FF3.6+ */
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,#7b7b7b), color-stop(100%,#5b5b5b)) !important; /* Chrome,Safari4+ */
background: -webkit-radial-gradient(center, ellipse cover,  #7b7b7b 0%,#5b5b5b 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-radial-gradient(center, ellipse cover,  #7b7b7b 0%,#5b5b5b 100%) !important; /* Opera 12+ */
background: -ms-radial-gradient(center, ellipse cover,  #7b7b7b 0%,#5b5b5b 100%) !important; /* IE10+ */
background: radial-gradient(ellipse at center,  #7b7b7b 0%,#5b5b5b 100%) !important; /* W3C */
}

.btn_bg_sort:hover {
background: #9b9b9b !important; /* Old browsers */
background: -moz-radial-gradient(center, ellipse cover,  #9b9b9b 0%, #7b7b7b 100%) !important; /* FF3.6+ */
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,#9b9b9b), color-stop(100%,#7b7b7b)) !important; /* Chrome,Safari4+ */
background: -webkit-radial-gradient(center, ellipse cover,  #9b9b9b 0%,#7b7b7b 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-radial-gradient(center, ellipse cover,  #9b9b9b 0%,#7b7b7b 100%) !important; /* Opera 12+ */
background: -ms-radial-gradient(center, ellipse cover,  #9b9b9b 0%,#7b7b7b 100%) !important; /* IE10+ */
background: radial-gradient(ellipse at center,  #9b9b9b 0%,#7b7b7b 100%) !important; /* W3C */
}

.btn_bg_sort.selected {
background: #5b5b5b !important; /* Old browsers */
background: -moz-radial-gradient(center, ellipse cover,  #5b5b5b 0%, #3b3b3b 100%) !important; /* FF3.6+ */
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,#5b5b5b), color-stop(100%,#3b3b3b)) !important; /* Chrome,Safari4+ */
background: -webkit-radial-gradient(center, ellipse cover,  #5b5b5b 0%,#3b3b3b 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-radial-gradient(center, ellipse cover,  #5b5b5b 0%,#3b3b3b 100%) !important; /* Opera 12+ */
background: -ms-radial-gradient(center, ellipse cover,  #5b5b5b 0%,#3b3b3b 100%) !important; /* IE10+ */
background: radial-gradient(ellipse at center,  #5b5b5b 0%,#3b3b3b 100%) !important; /* W3C */
}

#stationSortDate {
border-top: 1px solid #999 !important;
}

#stationSortAlpha {
border-top: 1px solid #999 !important;
border-left: 1px solid #999 !important;
}

.slidesForeground .slides {
width: 200% !important;
left: -100% !important;
padding: 0 !important;
}

.slidesForeground .slides .stationSlides {
left: 220px !important;
width: 100% !important;
}

.slidesBackground {
width: 100% !important;
background: #1b1b1b !important;
}

.slidesBackground img {
opacity: 0 !important;
}

.slidesBackground div img {
opacity: 0 !important;
}

#currentSlideArrow {
opacity: 0 !important;
width: 0 !important;
height: 0 !important;
}

#trackInfo {
background: #4b4b4b !important;
border: 0 !important;
margin-top: -4px !important;
}

#trackInfo .info {
color: #FFF !important;
text-shadow: 1px 1px 0 #3b3b3b !important;
}

#trackInfo .info a {
color: #FFF !important;
}

.fxCol-cont-5 {
background: none !important;
}

.fxCol-cont-1 {
background: none !important;
}

#trackInfoButtons {
  background-color: #2b2b2b !important;
}

#trackInfoButtons .buttons .button {
  border: 0 !important;
  border-radius: 0 !important;
  background-color: #4b4b4b !important;
  background-image: none !important;
  color: #fff !important;
}

#trackInfoButtons .buttons .button:hover {
  background-color: #5b5b5b !important;
}

.btn_bg {
  text-shadow: 1px 1px #000 !important;
}

.dropdownmenu ul {
  border-radius: 0 !important;
  background: #5b5b5b !important;
}

.dropdownmenu ul li:hover a {
  background: #6b6b6b !important;
}
  
.dropdownmenu ul li a {
  color: #fff !important;
}

#trackDetail a {
color: #DDD !important;
}

.detail .heading {
color: #FFF !important;
text-shadow: 1px 1px 0 #3b3b3b !important;
}

.detail .itemContent {
color: #FFF !important;
}

.detail .divider {
border-bottom: 1px solid #CCC !important;
}

#ad_container {
opacity: 0 !important;
width: 0 !important;
height: 0 !important;
}

#footer {
width: 100% !important;
margin: auto auto 10px auto !important;
}

#registeredNavFooter a {
color: #DDD !important;
}

#footer .legal {
color: #AAA !important;
}
}
