css:
@-moz-document domain("mmm-tasty.ru") {
body{
  cursor:auto !important;
}
a:hover{
  cursor:pointer !important;
  cursor: hand !important;
}
div#fixedbox_r_t{
  width:auto !important;
  top:0 !important;
  right:0 !important;
}
#pref_table{
  margin:0 !important;
  top:3px  !important;
  padding: 1px  !important;
  float:none!important;
}
.wrapper_pref div {
  text-align: right!important;
  white-space: nowrap;
  padding: 0 12px 20px 30px !important;
}
div.pref_png {
  background: url(http://a0.tcdn.ru/1694958325/images/pref_bg.png) no-repeat top left !important;
  opacity:1 !important;
  border-radius:0 ;
  max-height:1000px !important;
  margin:0!important;
  transition: none !important;
}
#pref_table td {
  white-space:nowrap;
  padding: 1px 2px;
  background:transparent !important;
  max-width:100px !important;
  max-height:100px !important;
  position:static !important;
}
td#pref_home,
td#pref_fav,
td#pref_ff,
td#pref_add,
td#pref_user,
td#pref_pref,
td#pref_exit{

}
#pref_table td IMG{
  opacity:1 !important;
  position: relative  !important;
  bottom: -1px !important;
  margin: 0 0px !important;
  display:inline-block !important;

}

#pref_table td#pref_user a{
  opacity:1 !important;
  position: relative;
  padding: 0 5px;
  bottom: 1px;
}

#pref_table td img.home {
  position:relative;
  bottom:-1px !important;
  margin:0 6px !important;
  left:-1px !important
}
#pref_table td #pref_fav a {
  position:relative !important;
  bottom:2px !important;
  margin:0pt 14px 0pt 0px !important;
  left: 4px !important;
}

#pref_table td #pref_ff a {
  padding-right: 4px !important;
  position: relative !important;
  top: 1px !important;
}

#pref_table td#pref_add {
	margin:0 2px 0 2px !important;
}
#pref_table td#pref_pref a {
	position:relative !important;
	bottom:-2px !important	
}
#pref_table td#pref_exit a {
	position:relative !important;
	right:0 !important;
	bottom:1px !important
}
#pref_table td#pref_user a {
  font-size:12px !important;
  color:white!important;
  opacity:1 !important;
  position:relative !important;
  padding:0 5px !important;
  bottom:1px !important;
  text-decoration:none;
}
#pref_table td#pref_user a:hover {
  background:transparent;
}
#pref_friends{
  left: 35px!important;
  top: -13px!important;
}
}
