css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(www.google.co.jp) {
    BODY {
        background-color: black !important;
        color:            white !important;
    }

    BUTTON, SELECT     { color: white !important; }
    INPUT[type=button] { color: white !important; }
    TEXTAREA           { color: white !important; }

    A:link    { color: red     !important; }
    A:visited { color: fuchsia !important; }
    A:hover   { color: lime    !important; }

    /** ロゴ画像変更. */
    DIV#hplogo {
        background-image: url(https://www.google.co.jp/logos/classicplus.png) !important;
    }
    /** 検索ボックス */
    DIV#gbqfqw,
    DIV.sfbgg {
        background-color: black !important;
        color: white !important;
    }
    /** 検索ボックス */
    DIV#gbx1 {
        background-color: transparent !important;
        color: white !important;
    }
    /** 検索ボックスの下 */
    DIV#hdtb, DIV#hdtbSum, DIV#hdtbMenus,
    DIV#appbar {
        background-color: transparent !important;
    }
    /** 左のリンク */
    DIV#leftnav {
        background-color: transparent !important;
    }
    DIV#ms > UL {
        background-color: transparent !important;
    }
    DIV#lc > UL {
        background-color: transparent !important;
    }
    UL#tbt {
        background-color: transparent !important;
    }
    /** 画像・地図・動画 ... */
    A.kl {
        background-color: transparent !important;
        color: white !important;
    }
    /** Change location */
    A#expand_location_link {
        background-color: transparent !important;
        color: white !important;
    }
    /* 日本語のページを検索、翻訳して検索 */
    A.q.qs {
        background-color: transparent !important;
        color: white !important;
    }
    /** Show search tools */
    A#tbpi {
        background-color: transparent !important;
        color: white !important;
    }
    /** 検索結果 */
    DIV.st      { color: white !important; }
    SPAN.st     { color: white !important; }
    .s em       { color: lime  !important; }
    A.l:link    { color: red     !important; }
    A.l:visited { color: fuchsia !important; }
    /* ###############################################  Google Reader */
    A.entry-title-link:link    { color: #6060ff !important; }
    A.entry-title-link:visited { color: #ff40ff !important; }
    /** [全て既読にする] 等のコントロールがある場所. */
    DIV#viewer-header {
        background-color: transparent !important;
    }
    /** 左ナビ全体 */
    .lhn-section, .lhn-section-primary, .lhn-section-footer {
        background-color: transparent !important;
    }
    /** 左ナビのリンク */
    #nav .link {
        background-color: transparent !important;
        color: blue !important;
    }
    /** ホーム、スター付きアイテム、トレンド、アイテムを参照 */
    #nav a .text {
        background-color: transparent !important;
        color: white !important;
    }
    /** ホーム */
    DIV#home-section {
        background-color: transparent !important;
    }
    /** 全てのアイテム */
    DIV#reading-list-selector {
        background-color: transparent !important;
    }
    /** スター付きアイテム ～ アイテムを参照 */
    DIV#lhn-selectors.section.lhn-section {
        background-color: transparent !important;
    }
    /** スター付きアイテム */
    DIV#star-selector {
        background-color: transparent !important;
    }
    /** トレンド */
    DIV#trends-selector {
        background-color: transparent !important;
    }
    /** アイテムを参照 */
    DIV#directory-selector {
        background-color: transparent !important;
    }
    /** アイテムを参照の下のフッタ */
    DIV.selectors-footer {
        background-color: transparent !important;
    }
    /** ピックアップ */
    DIV#lhn-recommendations {
        background-color: transparent !important;
    }
    /** ピックアップ項目間の線. */
    DIV#lhn-recommendations UL {
        background-color: transparent !important;
    }
    /** ピックアップ項目. */
    DIV#lhn-recommendations LI {
        background-color: transparent !important;
    }
    /** ピックアップ項目のリンク. */
    DIV#lhn-recommendations .name-text { color: white !important; }
    /** 全てのおすすめを表示 */
    A#recommendations-tree-view-all { color: white !important; }
    /** 左下登録フィード. なくても良いが、transparent 以外にしないこと。 */
    DIV#lhn-subscriptions {
        background-color: transparent !important;
    }
    /** 左下登録フィードヘッダ. */
    SPAN#sub-tree-header { color: white !important; }
    /** 左下登録フィード.	ヘッダの最小化ボタンのアイコン画像を変更. */
    DIV#lhn-subscriptions-minimize {
        background-position: -12px -40px !important;
        left:  3px !important;
        top:   8px !important;
    }
    /** 登録フィード項目間の線. */
    DIV#lhn-subscriptions UL {
        background-color: transparent !important;
    }
    /** 登録フィード項目. */
    DIV#lhn-subscriptions LI {
        background-color: transparent !important;
    }
    /** 登録フィード項目のリンク. */
    DIV#lhn-subscriptions .name-text   { color: white !important; }
    /** 左下登録フィードで未読の記事があるブログ. */
    DIV#lhn-subscriptions .name-unread { color: red !important; }
    /** 左下登録フィードの分類フォルダの展開. */
    DIV#lhn-subscriptions .folder-toggle {
        background-position: -12px -40px !important;
        left: 14px !important;
        top:   4px !important;
    }
    /** すべてのアイテム 記事一覧のコンテナ. */
    DIV#viewer-container {
        background-color: transparent !important;
        color: white !important;
    }
    DIV#viewer-entries-container {
        background-color: transparent !important;
        color: white !important;
    }
    #entries .entry {
        background-color: transparent !important;
        color: white !important;
    }
    /** 記事一覧. */
    DIV.collapsed {
        background-color: transparent !important;
        font-size: large !important;
    }
    /** ブログ名. */
    .entry-source-title { color: white !important; }
    /** 記事名. */
    .entry-title {           /* --- 未読記事 */
        color: white !important;
        font-weight: normal !important;
    }
    DIV.read .entry-title {  /* --- 既読記事 */
        color: gray  !important;
        font-weight: normal !important;
    }
    DIV#current-entry {
        background-color: transparent !important;
        color: white !important;
    }
    /** 記事概要. */
    .entry-container {
        background-color: transparent !important;
        color: white !important;
        font-size: large;
    }
    .entry-body {
        color: white !important;
        font-size: large;
    }
    DIV#current-entry SPAN {
        color: white !important;
        font-size: large;
    }
    .entry-body font[color='#000000'] {
        color: white !important;
        font-size: large;
    }
    /** 記事へのアクション. */
    .entry-actions {
        background-color: transparent !important;
    }
    /** メール. */
    SPAN.email SPAN {
        color: white !important;
    }
    /** 未読のままにする. */
    SPAN.read-state-not-kept-unread {
        color: white !important;
    }
    /** タグを追加. */
    SPAN.tag {
        color: white !important;
    }
    .entry-tagging-action-title {
        color: white !important;
    }
}
