css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
    BODY {
        background-color: black !important;
        color:            white !important;
    }

    BUTTON, SELECT     { color: white !important; }
    INPUT[type=button] { color: white !important; }
    INPUT[type=text]   { color: white !important; }
    TEXTAREA           { color: white !important; }

    A:link    { color: red     !important; }
    A:visited { color: fuchsia !important; }
    A:hover   { color: lime    !important; }

    DIV, UL, LI {
        background-color: transparent !important;
    }
    /** プレイリストのボタン. */
    BUTTON.yt-uix-button-default {
        color: black !important;
    }
    /** もっと見るボタン.*/
    DIV#watch-description-expand BUTTON.yt-uix-button-text {
        color: black !important;
    }
    /** コメント投稿ボタン */
    BUTTON.yt-uix-button-hh-default { color: black !important; }
    BUTTON.yt-uix-button-hh-text    { color: black !important; }
    /** 最上部. */
    DIV#masthead-container {
        background-image: none !important;
        background-color: transparent !important;
    }
    /** ユーザートップページの動画紹介. */
    DIV.branded-page-v2-col-container {
        background-image: none !important;
    }
    /** トップページの動画説明. */
    .description { color: white !important; }
    /** 各動画のタイトル. */
    #watch-headline-title { color: white !important; }
    /** 各動画の動画説明. */
    #watch-description { color: white !important; }
    /** 各動画のタイトル. */
    SPAN.yt-uix-expander-head { color: white !important; }
    /** 各動画の再生数. */
    SPAN.watch-view-count { color: white !important; }
    /* 再生リストのメモのテキストエリア */
    #watch-video-annotation-textarea {
        background-color: transparent !important;
        color: white !important;
    }
    /* コメントのテキストエリア */
    TEXTAREA {
        background-color: transparent !important;
        color: white !important;
    }
    /** 各動画のコメント. */
    #watch7-discussion { color: white !important; }
    .yt-tile-default   { color: white !important; }
    /** 関連動画. */
    LI.video-list-item A:hover {
        background-color: #202020 !important;
        border-radius: 10px !important;
    }
    /** 関連動画タイトル. */
    LI.video-list-item SPAN.title { color: white !important; }
    /** プレイリスト. */
    #playlist-pane-container {
        background-image: none  !important;
        background-color: transparent !important;
    }
    /** プレイリスト内動画タイトル. */
    .video-title { color: white !important; }
    #playlist-bar-bar-container {
        background-image: none  !important;
        background-color: black !important;
        opacity: 0.8 !important;
    }
    #playlist-bar-tray-container {
        background-image: none  !important;
        background-color: black !important;
    }
    /** フッター. */
    DIV#footer-hh-main BUTTON { color: black !important; }
}
