css:
/* Pitchfork Nonsense Removal by Babkock
 * Last updated: 20 December, 2012 */
@namespace url(http://www.w3.org/1999/xhtml); 

@-moz-document domain(pitchfork.com) {

* { -moz-transition:none !important; }

#header #ticker { display:none !important; }
#title #icons { display:none !important; }
.hud { display:none !important; }
#nav ul li ul { display:none !important; }
ul.social { display:none !important; }
#family-nav { display:none !important; }
div.more-content { display:none !important; }
#hp-features { display:none !important; }

/* remove the sidebar, widen the main content */
#side { display:none !important; }

#main { width:100% !important; }
ul.review-meta, div.info { width:100% !important; }

/* remove some boxes on the home page */
#hp-trending.box, #hp-trending-header { display:none !important; }
#hp-bnm.box, #hp-bnm-header { display:none !important; }
#hp-tracks.box, #hp-tracks-header { display:none !important; }
#hp-features-control, #hp-features-header { display:none !important; }

/* best new music list */
.object-list.bnm-list div.info { width:80% !important; }
.object-list div.info { width:80% !important; }

.bnm-hub-featured ul { width:100% !important; }
.bnm-hub-featured li.baseline { padding-left:5% !important; padding-right:5% !important; padding-top:2px !important; }

ul.bnm-hub-headers { margin-left:15% !important; margin-right:15% !important; }

.bnm-hub-subfeatured li.baseline { padding-left:5% !important; padding-right:5% !important; height:auto !important; }

/* Pitchfork TV page */
#page.tv li.partners { background:none !important; }

/*
ul.object-grid.episode-grid li ul,
ul.object-grid
{ padding-left:15% !important; padding-right:15% !important; }
*/
ul.object-grid li { padding-left:3% !important; padding-right:3% !important; }

.features-hub ul.list div.info { width:312px !important; } 
.object-detail .info { width:auto !important; }

ul.object-prevnext { width:100% !important; }
ul.object-prevnext li.prev { border-right:0 !important; }

/* artists page */
div#artist-list .object-grid li { padding-left:0px !important; padding-right:0px !important; }

/* remove icky outlines */
a:hover, a:active, a:focus { outline:0 !important; }

}
