css:
@-moz-document url-prefix("http://www.amazon.com") {
.list div.image {
  width:75px !IMPORTANT; overflow:hidden;
  -webkit-transition: width .5s;
}

.list div.image:hover {
  width:160px !IMPORTANT;
}

.list .image img {
  opacity:.2;
  -webkit-transition: opacity .5s;
  
}
.list .image img:hover {
  opacity:1;
}



.grid div.image {
  height:75px !IMPORTANT; overflow:hidden;
  -webkit-transition: height .5s;
}

.grid div.image:hover {
  height:160px !IMPORTANT;
}

.grid .image img {
  opacity:.2;
  -webkit-transition: opacity .5s;
  
}
.grid .image img:hover {
  opacity:1;
}


#main-image-wrapper img {
  opacity:.2;
  -webkit-filter: blur(5px) contrast(140%);
  -webkit-transition: opacity .5s;
}
#main-image-wrapper img:hover {
  -webkit-filter: blur(0);
  opacity:1;
}


.frontWindowProductImageContent img {
  opacity:.2;
  -webkit-transition: opacity .5s;
}
.frontWindowProductImageContent img:hover {
  opacity:1;
  -webkit-transition: opacity .5s;
}


.shoveler .product-image {
  height:50px;
  -webkit-transition: height .5s;
  overflow:hidden;
}
.shoveler .product-image:hover {
  height: 130px;
}
.shoveler-content img {
  opacity: .2;
  -webkit-transition: opacity .5s;
}
.shoveler-content img:hover {
  opacity: 1;
}
}
