css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(listia.com) {

* {
 text-decoration: none!important;
 text-shadow: none!important;
}

#header {
 position: fixed!important;
 top: 0!important;
 margin: auto!important;
 width: 100%!important;
 z-index: 99999!important;
}

.container_12 {
 margin-top: 60px!important;
}

#pre_footer, body.search div.ad-skyscraper, [style="width: 728px; height: 90px;"], #trialpay_dealspot, .summary_title, [href="http://www.listia.com/account/buy_credits"], [id*='div-gpt-ad'], span.gray b, .ad, body.search div.ad-bottom-content, .ad-bottom {
 display: none!important;
}

html, body, #top_container, #main_box, div.rounded_left_content, div.rounded_left_b, div.rounded_right_content, div.rounded_right_b, #footer_wrapper, div.listing_off, .banner, #browse_dropdown div, .clear-fix, body.search div.filter, #ads ul li, body.search div.ad-bottom-content, div.rounded_right, #header_and_contents, #account_content {
 background: #000!important;
}

div.rounded_page_content {
 background: #222!important;
}

#header {
 background: -moz-linear-gradient(top, #000000 40%, #fced49 100%)!important;
}

div.mm_box:not(#search_box):hover {
 color: #000!important;
 background: -moz-linear-gradient(top, #fced49 10%, #000 100%)!important;
}

a, p, span, div.menu_collapse, h2, body.search div.fblike div.text, label, div.listing_time, body.search div.rewards-description div.description {
 color: #fff!important;
}

div.fb_friend_prompt {
 color: #f0f!important;
}

[href]:hover, span.bold, div.listing_title a, h1, h3, h3 a, .adt span, .guider_content h2, span.title a, div.title, body.search div.rewards-category {
 color: #ff0!important;
}

.menu_link a, .menu_link a span {
 color: #000!important;
}

div.width_100p_text, div.menu_collapse:hover, .summary_title, div.summary-block h2, .home_nav_l, div.listing_on:hover, div.menu:hover, div.browse_content li:hover, .guider, body.search div.filter ul li.sub:hover, body.search ul.side-promoted li.listing:hover, body.search ul.auction-gallery li.auction, #ads ul li:hover, body.search ul.button-list li a, .ad-bottom, div.get_started_corner_content, body.search div.filter ul li:hover, #account_name, #header div.top div.top-menu #staticbar-ex-logo #static_links #account_menu #account_content ul li a:hover {
 color: #ff0!important;
 background: -moz-linear-gradient(#500, #000, #500)!important;
 border-radius: 5px!important;
 box-shadow: inset 0px 0px 15px #000!important;
}

body.search ul.auction-gallery li.auction:hover, body.search ul.button-list li a:hover, div.get_started_corner_content:hover {
 background: -moz-linear-gradient(#900, #000, #900)!important;
}

.home_nav_r {
 color: #ff0!important;
 background: -moz-linear-gradient(#500, #000, #500)!important;
 border-radius: 5px!important;
 height: 20px!important;
 box-shadow: inset 0px 0px 15px #000!important;
}

.button, div.flash_notice, body.search div.filter-categories li.current-category, body.search ul.button-list a.on, div.pagination em {
 color: #ff0!important;
 background: -moz-linear-gradient(#050, #000, #050)!important;
 border-radius: 5px!important;
 box-shadow: inset 0px 0px 15px #000!important;
}

.button:hover, body.search ul.button-list a.on:hover {
 background: -moz-linear-gradient(#090, #000, #090)!important;
}

.guider_button, .submit_blue {
 color: #ff0!important;
 background: -moz-linear-gradient(#005, #000, #005)!important;
 border-radius: 5px!important;
 box-shadow: inset 0px 0px 15px #000!important;
}

.guider_button:hover, .submit_blue:hover {
 background: -moz-linear-gradient(#009, #000, #009)!important;
}

img {
 opacity: .7!important;
}

img:hover {
 opacity: 1!important;
}


}
