css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.herozerogame.com") {


#ipbwrapper {

    background:url(http://www.v3wall.com/wallpaper/1920_1080/0911/1920_1080_20091117085749395769.jpg) fixed no-repeat center top !important;
    
}


#content {

    background: -moz-linear-gradient(top,  #a2d6e8 4%, #71ceef 40%, #21b4e2 60%, #a1d7ea 100%) !important;
    position: relative !important;
    left: 170px;
    color:#000!important;
    -moz-border-radius:10px !important;
    margin:2px !important;
    border:1px solid !important;
    border-color:#000 !important;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px !important;
    text-shadow:#000 0px 0px 0px;
    
}

#header_bar {

    background: -moz-linear-gradient(top,  #a2d6e8 4%, #71ceef 40%, #21b4e2 60%, #a1d7ea 100%) !important;
    text-shadow:#000 2px 2px 2px;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px !important;
    -moz-border-radius-bottomleft:10px !important;
    -moz-border-radius-bottomright:10px !important;
    
}

#community_app_menu > li > a {

    background: -moz-linear-gradient(top,  #a2d6e8 4%, #71ceef 40%, #21b4e2 60%, #a1d7ea 100%) !important;
    position: relative !important; top: 1px;
    text-shadow:blue 2px 2px 4px !important;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px !important;
    -moz-border-radius-topleft:10px !important;
    -moz-border-radius-topright:10px !important;

}

#community_app_menu > li.active > a {

    color:blue !important;
   
}


.maintitle {

    background: -moz-linear-gradient(top,  rgba(30,87,153,1) 0%, rgba(41,137,216,1) 50%, rgba(30,87,153,1) 100%) !important;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px inset!important;
    border-top: 2px solid !important;
    border-color:black !important;
    -moz-border-radius-topleft:10px !important;
    -moz-border-radius-topright:10px !important;

}    

#search_wrap {

    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px !important;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px inset!important;
    -moz-border-radius:10px !important;
  
}

.general_box h3 {

    background: -moz-linear-gradient(top,  rgba(179,220,237,1) 0%, rgba(41,184,229,1) 50%, rgba(188,224,238,1) 100%) !important;
    -moz-border-radius:8px !important;
    border: 1px solid !important;
    border-color:black !important;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px !important;

}


}
