.xt{position:relative;overflow:hidden;width:15px;height:15px;float:right;cursor:pointer;background:transparent url('http://s49.radikal.ru/i124/1212/64/3fb57fe08c2e.gif') no-repeat;margin-left:2px;margin-top:4px;}
.xt-close{background-position:0 0;}
.xt-close-over{background-position:-15px 0;}
.xt-mini{background-position:0 -15px;}
.xt-mini-over{background-position:-15px -15px;}
.xt-maxi{background-position:0 -30px;}
.xt-maxi-over{background-position:-15px -30px;}
.xt-rest{background-position:0 -45px;}
.xt-rest-over{background-position:-15px -45px;}
.xw-hdr{text-align:left;white-space:nowrap;padding:1px 0 0 0;white-space:nowrap;zoom:1;height:24px}
.xw-hdr-text{vertical-align:3px;cursor:default;color:#212121;font:bold 11px tahoma,arial,verdana,sans-serif;line-height:20px;}
.xw-sps {height:4px;overflow:hidden;position:absolute;width:100%;z-index:1}
.xw-tsps{height:6px}
.xw-resize .xw-tl {cursor:nw-resize}
.xw-resize .xw-tr {cursor:ne-resize}
.xw-resize .xw-sps {cursor:n-resize}
.xw-resize .xw-ml {cursor:w-resize}
.xw-resize .xw-mr {cursor:e-resize}
.xw-resize .xw-mc {cursor:default}
.xw-resize .xw-bl {cursor:sw-resize}
.xw-resize .xw-br {cursor:se-resize}
.xw-resize .xw-bc {cursor:s-resize}
.xw-tc{background:transparent url('http://s018.radikal.ru/i508/1212/54/e1bb5ca7a50a.png') repeat-x 0 0;overflow:hidden;position:relative;font-size:0;line-height:0;}
.xw-tl{background:transparent url('http://i052.radikal.ru/1212/e3/09e279777708.png') no-repeat 0 0;padding-left:6px;position:relative;}
.xw-tr{background:transparent url('http://s05.radikal.ru/i178/1212/63/8c97371499a7.png') no-repeat right 0;padding-right:6px;position:relative;}
.xw-bc{background:transparent url('http://s018.radikal.ru/i508/1212/54/e1bb5ca7a50a.png') repeat-x 0 bottom;zoom:1;font-size:0;line-height:0;overflow:hidden;}
.xw-bcm{height:2px;background:transparent url('http://s018.radikal.ru/i508/1212/54/e1bb5ca7a50a.png') repeat-x 0 bottom;}
.xw-bc .xw-footer{padding-bottom:6px;font-size:0;line-height:0;}
.xw-bcm .xw-footer{padding-bottom:2px;font-size:0;line-height:0;}
.xw-bl{background:transparent url('http://i052.radikal.ru/1212/e3/09e279777708.png') no-repeat 0 bottom;padding-left:6px;}
.xw-br{background:transparent url('http://s05.radikal.ru/i178/1212/63/8c97371499a7.png') no-repeat right bottom;padding-right:6px;}
.xw-mc{border:1px solid #BCBFC4;padding:0;margin:0;font:normal 11px tahoma,arial,helvetica,sans-serif;background:#D7D7D7;}
.xw-ml{background:transparent url('http://s006.radikal.ru/i214/1212/b8/813bc296450b.png') repeat-y 0 0;padding-left:6px;}
.xw-mr{background:transparent url('http://s006.radikal.ru/i214/1212/b8/813bc296450b.png') repeat-y right 0;padding-right:6px;}
.xw-bc {height:6px;}
.xw-icon{width:16px;margin:2px 2px 0 0;height:16px;}
.xw-body{background:transparent}
.xw-draggable,.xw-draggable .xw-hdr-text{cursor:move;}
.xw-plain .xw-mc{background:#fff;border-right:1px solid #EAEBEC;border-bottom:1px solid #EAEBEC;border-top:1px solid #C2C5CA;border-left:1px solid #C2C5CA;}
.xw-plain .xw-body{padding:3px;border-left:1px solid #EAEBEC;border-top:1px solid #EAEBEC;border-bottom:1px solid #C2C5CA;border-right:1px solid #C2C5CA;background:transparent!important;}
.xw-blank {background:transparent url('http://s018.radikal.ru/i508/1212/54/e1bb5ca7a50a.png');overflow:hidden;opacity:0.7 !important;}
.xw-draging .xw-bcm {opacity:0.7 !important;}
.myWinCont * {color:#333 !important;outline:none !important;}
.myWinCont input, .myWinCont textarea, .myWinCont select {background:#fff !important;}
.myWinPollT {background:#c0c0c0 !important;}
.myWinPollT td {background:#fff;}
.myWinPollG {background: url('http://zismo.biz/.s/img/wd/3/gridh.gif') no-repeat 0 0 !important;}
 
 
 
 
