css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://b.hatena.ne.jp/add?mode=confirm") {
  body { color: #333 !important; }
  #banner, form.searchform input, #bannersub, p.addurl-footer a img, td br, span.note, #candidates_list div div, #keywords_list, ul[style] { display: none; }
  #container { margin: 1em auto !important; width: 95%; }
  #body { width: auto !important; }
  h2.title { background: url("data:image/gif;base64,R0lGODlhDAAMALMPAFBQUBAQEKCgoODg4CAgIDAwMHBwcGBgYEBAQLCwsH9/f9DQ0JCQkMDAwP///////yH5BAEAAA8ALAAAAAAMAAwAAAQ38MlJ60yCqWWT+4NRCd/BOAd1fowHTMrXfB8iGfIyO/YTOw1dDfYROgoVHC1geRw+zOYDQGhGAAA7") center left no-repeat !important; padding: 0 0 0 14px !important; border: none !important; font-size: 100% !important; }
  div.info { margin-left: 0 !important; }
  td.label { background: transparent !important; color: #333 !important; padding-left: 0 !important; }
  p.addurl-footer { display: inline; }
  td p[style="margin: 0.3em;"] { display: inline; background: #333; color: #ccc;  margin: 0 !important; padding: 0.25em; -moz-border-radius: 0.25em; }
  td p[style="margin: 0.3em;"] a { color: #fff; }
  #comment_count { color: #999; }
  #candidates_list { position: absolute; top: 0; left: 0; height: 0 !important; width: 100%; }
  #candidates_list div { background: #333 !important; color: #fff; margin: 0 !important; padding: 0.5em; border: none !important; width: auto !important; opacity: 0.8; }
  #tags_completion span[id^="tag"] { -moz-border-radius: 0.25em; }
}
