css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://forums.riftgame.com"),
url("http://forums.riftgame.com"),  url-prefix("http://forums.riftgame.com"),
url-prefix("http://forums.riftgame.com") {
 body { 
 margin: 0 !important;
 padding: 0 !important;
 width: 100% !important;
 font-family: Calibri, Tahoma, Verdana, Arial, sans-serif !important;
}
.body_wrapper {
  margin: 0 auto !important;
  width: 90% !important; 
  max-width: 90% !important;
  background-color: rgba(0, 0, 0, .5) !important;
  moz-border-radius: 5px;
  border-radius: 5px;
  background-image: none !important;
  margin-top: 30px !important;
  padding-top: 5px !important;
}
.postlist.restrain, .above_postlist,ol#forums.floatcontainer, .floatcontainer, .navlinks,
.above_threadlist, .announcements, .forumbits, .threadlist, 
.noinlinemod.below_threadlist, .forum_info.block, .noinlinemod.below_postlist, .vbform, .group,
.block.searchresults, #searchform, .member_content.userprof.fullwidth {
 width: 96% !important;
 margin: 0 auto !important;
}

.postcontent {
 font-family: Calibri, Tahoma, Verdana, Arial, sans-serif !important;
 line-height: 1.5em;
}
 .postcontent ul li, .postcontent ol li {
    margin-bottom: 10px !important;
 }
.postlist {
 margin: 0 auto !important;
}
#pagetitle.pagetitle h1 {
 margin-left: -10px !important;
 padding: 10px 0 !important;
 font-size: 15px !important;
}
#navtabs.navtabs.floatcontainer {
 padding-top: 25px !important;
 padding-left: 110px !important;
}
.postfoot {
 margin: 0 auto !important;
 background: #204154 !important;
}
.textcontrols.floatcontainer {
 width: 100% !important;
 float: right !important;
 padding-top: 5px !important;
 padding-bottom: 5px !important;
 margin-right: -1px !important;
}
.above_threadlist {
 padding-top: 20px !important;
 float: none !important;
}
.below_threadlist {
 float: none !important;
}
#forum_info_options {
 float: none !important;
 margin: 0 auto !important;
}
  .forum_info_form {
   margin-bottom: 20px !important;
  }
/* Forum Index Footer */
.blockhead {
 width: 95% !important;
 margin: 0 auto !important;
 margin-left: -2px !important;
 }
  h2.blockhead { 
   padding-top: 5px !important;
   padding-bottom: 5px !important;
 }
    .subforumdescription {
       background: #0c4e5e !important;
    }
#wgo {
 margin-left: 10px !important;
}
.wgo_subblock {
 padding-top: 10px !important; 
}


/* User Info Sidebar */
.usertitle, .userinfo_extra {
 font-size: 14px !important;
}
.postuseravatar {
 margin-bottom: 5px !important;
}
.signature {
 color: #b2b2b2 !important;
 font-size: 14px !important;
 line-height: 1.5em !important;
}
.posthead {
 padding-top: 10px !important;
 padding-bottom: 10px !important;
}
.nodecontrols {
 padding-top: 0px !important;
 margin-top: 10px !important;
}
#breadcrumb.breadcrumb, .above_postlist, #pagetitle.pagetitle, #pagetitle   {
 width: 95% !important;
 margin: 0 auto !important;
 float: none !important;
}
#above_threadlist_controls.above_threadlist_controls.toolsmenu {
 width: 96% !important;
 margin-left: 35px !important;
}
.thread_controls, .above_postlist {
 float: none !important;
 clear: both !important;
 background: transparent !important;
 border: none !important;
}
#thread_controls.thread_controls.toolsmenu div {
 margin-top: 10px !important;
}
#vbseo-likes {
 height: 50px !important;
}
.pagination_top {
 padding-top: 10px !important;
}
#breadcrumb {
 padding-top: 5px !important;
 margin-top: 5px !important;
}
#breadcrumb.breadcrumb ul.floatcontainer {
 margin-left: 0 !important;
 width: 100% !important;
}
#pagination_bottom, #below_postlist.noinlinemod.below_postlist {
 float: none !important;
 margin-bottom: 10px !important;
}
.options_block_container {
 float: none !important;
 margin: 0 auto !important;
 }
li.navbit.lastnavbit {
 font-weight: bold !important;
}
#vbseo-likes-counts {
 margin-right: -20px !important;
}
.bbcode_quote {
 border: 0 !important;
}
.quote_container {
 padding: 10px !important;
 background: #272a2c !important;
 border-radius: 4px !important;
 -moz-border-radius: 4px !important;
}
.postbody {
 border-left: 1px solid #204154 !important;
}
#thread_info {
 float: none !important;
 width: 95% !important;
 margin: 0 auto !important;
 margin-top: 50px !important;
}
.threadinfo {
 margin-bottom: 5px !important;
}
  .threadmeta .author { 
    padding-top: 1px !important;
  }
 
.navlinks {
 margin-top: 20px !important;
}
.threadpagenav {
 margin-bottom: 20px !important;
}
/* Forum Listing */
.datacontainer{
 padding-top: 12px !important;
}
.forumhead {
 padding-top: 5px !important;
 padding-bottom: 5px !important;
}
.forumicon {
 padding-top: 5px !important;
}
h2.forumtitle {
 font-size: 15px !important;
 padding-bottom: 3px !important;
}
.forumdescription {
 font-size: 13px !important;
}
/* Thread View */
#newreplylink_bottom {
 margin-top: 10px !important;
}

/* Forum Listing View view */
.nonsticky, .sticky {
 padding-top: 5px !important;
 padding-bottom: 5px !important;
}
.threadstatus {
 margin-right: 10px !important;
 margin-left: 10px !important;
}

/* Subforum Threads View */
.announcerow {
 border-radius: 4px !important;
 -moz-border-radius: 4px !important;
 background: #0c596d !important;
}
 .announcement, .announceinfo {
  background-color: transparent !important;
  padding: 5px !important;
 } 
  .sticky {
      background: #000 !important;
  } 
  .announcement { 
      width: 600px !important;
   }
   .announceinfo {
      width: 300px !important;
      float: right !important;
    }
#forumbits, #threadlist {
 margin: 0 auto !important;
 float: none;
}

/* Form */
.section {
 width: 95% !important;
 margin: 0 auto !important;
 float: none !important;
}
code {
 background: #333 !important;
}
}
