css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

#main-window[sizemode=fullscreen] #navigator-toolbox:not(:hover) > #nav-bar {
    visibility :collapse !important;
    }
#main-window[sizemode=fullscreen] #navigator-toolbox {
    padding-top: 0.1px !important;
    background : transparent !important;
    }

#main-window[sizemode=fullscreen] hbox tabbrowser .tabbrowser-strip:not(:hover) {
    height: 2px;
    }

#main-window[sizemode=fullscreen] hbox tabbrowser .tabbrowser-strip:not(:hover) .tabbrowser-tabs {
    visibility :collapse !important;
    }

#main-window[sizemode=fullscreen] hbox tabbrowser .tabbrowser-strip {
    padding-top        : 0.1px !important;
    background-color   : rgba(10, 10, 10, 0.95) !important;
    position           : fixed !important;
    left               : 0%;
    bottom             : 0%;
    width              : 100%;
    margin-bottom      : -1px;
    }

#main-window[sizemode=fullscreen] .tabbrowser-tabs {
    width: 100%;
    }
