css:
@-moz-document domain("vkontakte.ru") {

/* Main Layout **********************************************/

.upArrow, .dArrow

{ position: relative !important;

  top: -39px !important;

  right: -10px !important;}

#sideBar #nav ol li

{ margin-left: 0 !important;}

#sideBar #nav li

{ margin-left: -2.3em !important;}

#moreWall

{ display: block !important;

  margin: -25px 0 0 0 !important;

  padding-right: 5px !important;

  text-align: right !important;}

a.wallFunc

{ background: none !important;}

#wall .fSub h3 div, .wallpost .info .header b

{ display: none !important;}

#wall .fSub h3 div:first-child, #quickReply #r, #quickReply #br 

{ display: block !important}

#quickReply #br .buttonRow

{ float: left !important}

#myLink, #bFooter, #bNav

{ display:none !important;} 

li[style^="width:"]

{ float:left !important}

#quickSearch

{ float:left !important;

  padding:9px 9px 0px 9px !important;}

#userProfile

{ width:100% !important;}

a[href="index.php"], .playline

{ display:none !important;}

#rateRight

{ display:none !important;}

#percent

{ width:50px !important;}

#wrapHI #header

{ overflow:hidden !important;

  height:12px !important;

  line-height:14px !important;

  width:100% !important;

  position:fixed !important;

  z-index:99 !important;

  left:100px !important;}

#content

{ padding-top:20px !important;

  padding-left:121px !important;

  border:none !important;}

#pageLayout

{  width:100% !important;}

#pageHeader

{ width:100% !important;

  position:fixed !important;

  z-index:99 !important;

  background-image:none !important;}

#sideBar

{ padding:14px 0% 0% 0% !important;

  margin:32px 0% 0% 0% !important;

  width:89px !important;

  position:fixed !important;

  z-index:99 !important;}

#pageBody

{ margin-top:45px !important;

  width:100% !important;}

#leftColumn 

{ margin-right:0% !important;

  width:200px !important;

  position:absolute !important;

  left:100px !important;}

#rightColumn 

{ margin-left:5px !important;

  width:auto !important;

  position:absolute !important;

  left:300px !important;}

#wrap1

{ border:none !important;}

#mainFeed

{ margin-left: 6px !important;}



/* Layout for userinfo ***********************************************/

.friendTable td

{ text-align:left !important;

  vertical-align:top !important;}



/* Remove paddings at left pannel ************************************/

.aPad,

.flexBox

{ padding:0px !important;}

table[class^="friendTable"] > tbody> tr > td > table > tbody > tr > td

{ text-align:left !important;}

.dataWrap

{ width:100% !important;}



/* Layout for group list *********************************************/

#groupslist .grouprow

{ background:white none repeat scroll 0% !important;

  border:1px solid #CCCCCC !important;

  padding:1% 1% 1% 1% !important;

  margin:-1px !important;

  width:98% !important;}

#groupslist .tunaimage

{ text-align:left !important;

  vertical-align:top !important;

  padding:0% 0% 0% 0% !important;

  margin:0% 0% 0% 0% !important;

  width:10% !important;}

#groupslist .info

{ vertical-align:top;

  padding:0% 0% 0% 0%  !important;

  margin:0% 0% 0% 0% !important;

  width:80% !important;}

#groupslist .actions

{ vertical-align:top !important;

  padding:0% 0% 0% 0% !important;

  margin:0% 0% 0% 0% !important;

  width:10% !important;}  

#startagroup

{ padding:1% 1% 1% 1% !important;

  margin:0% 0% 0% 0% !important;

  width:48% !important;}

#findagroup

{ float:right !important;

  padding:1% 1% 1% 1% !important;

  margin:0% 0% 0% 0% !important;

  width:48% !important;}



/* Layout for friend-list ********************************************/



td[width="110"]

{ width:10% !important;}

td[width="290"]

{ width:80% !important;}

td[width="200"]

{ width:10% !important;}

.result .image

{ width:auto !important;}

.result .info

{ width:auto !important;}

ui[id="nav"]

{ width:auto !important;}

.result .info dd

{ width:auto !important;}

}
