css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rainbowdash.net") {
/* Avatars and site_notice>p drift */

#site_notice p {position: fixed; top: 0px; left: 0px; font-size: 50pt; z-index: 1337;width:900px;
animation: ass 1s infinite;
-moz-animation: ass 1s infinite;
-o-animation: ass 1s infinite;
-webkit-animation: ass 1s infinite;
}
li:nth-of-type(4n) img{position:relative;
top:0px;
float:none !important;
animation: ass 2s infinite;
-moz-animation: ass 2s infinite;
-o-animation: ass 2s infinite;
-webkit-animation: ass 2s infinite;
}
li:nth-of-type(4n+1) img{position:relative;
top:0px;
float:none !important;
animation: ass 3s infinite;
-moz-animation: ass 3s infinite;
-o-animation: ass 3s infinite;
-webkit-animation: ass 3s infinite;
}
li:nth-of-type(4n+2) img{position:relative;
top:0px;
float:none !important;
animation: ass 5s infinite;
-moz-animation: ass 5s infinite;
-o-animation: ass 5s infinite;
-webkit-animation: ass 5s infinite;
}
li:nth-of-type(4n+3) img{position:relative;
top:0px;
float:none !important;
animation: ass 7s infinite;
-moz-animation: ass 7s infinite;
-o-animation: ass 7s infinite;
-webkit-animation: ass 7s infinite;
}
#site_nav_global_primary{position:fixed;
top:0px;
float:none !important;
z-index: 1338;
animation: ass 20s infinite;
-moz-animation: ass 20s infinite;
-o-animation: ass 20s infinite;
-webkit-animation: ass 20s infinite;
}
@keyframes ass
{
0% {top: 0%;color:black}
50% {top: 100%;color:white}
100% {top: 0%;color:black}
}
@-moz-keyframes ass
{
0% {top: 0%;color:black}
50% {top: 100%;color:white}
100% {top: 0%;color:black}
}
@-webkit-keyframes ass
{
0% {top: 0%;color:black}
50% {top: 100%;color:white}
100% {top: 0%;color:black}
}
@-o-keyframes ass
{
0% {top: 0%;color:black}
50% {top: 100%;color:white}
100% {top: 0%;color:black}
}

/* Timeline and sidebar do the shuffle */
#content
{
position: relative;
left: 0px;
z-index: 1000;
-moz-animation: butt 5s infinite;
-o-animation: butt 5s infinite;
-webkit-animation: butt 5s infinite;
animation: butt 5s infinite;
}

#aside_primary, #site_nav_local_views
{
position: relative;
right: 0px;
z-index: 0;
-moz-animation: glute 5s infinite;
-o-animation: glute 5s infinite;
-webkit-animation: glute 5s infinite;
animation: glute 5s infinite;
}

@-moz-keyframes butt
{
0%{left: 0%;z-index:1000}
50%{left: 30%;z-index:1000}
51%{z-index:0}
100%{left: 0%;z-index:0}
}

@-moz-keyframes glute
{
0%{right: 0%;z-index:0}
50%{right: 69%;z-index:0}
51%{z-index:1000}
100%{right: 0%;z-index:1000}
}

@-o-keyframes butt
{
0%{left: 0%;z-index:1000}
50%{left: 30%;z-index:1000}
51%{z-index:0}
100%{left: 0%;z-index:0}
}

@-o-keyframes glute
{
0%{right: 0%;z-index:0}
50%{right: 69%;z-index:0}
51%{z-index:1000}
100%{right: 0%;z-index:1000}
}

@-webkit-keyframes butt
{
0%{left: 0%;z-index:1000}
50%{left: 30%;z-index:1000}
51%{z-index:0}
100%{left: 0%;z-index:0}
}

@-webkit-keyframes glute
{
0%{right: 0%;z-index:0}
50%{right: 69%;z-index:0}
51%{z-index:1000}
100%{right: 0%;z-index:1000}
}

@keyframes butt
{
0%{left: 0%;z-index:1000}
50%{left: 30%;z-index:1000}
51%{z-index:0}
100%{left: 0%;z-index:0}
}

@keyframes glute
{
0%{right: 0%;z-index:0}
50%{right: 69%;z-index:0}
51%{z-index:1000}
100%{right: 0%;z-index:1000}
}

/* Everything spins */
#form_notice
{
-moz-animation: flank 5s linear infinite;
-o-animation: flank 5s linear infinite;
-webkit-animation: flank 5s linear infinite;
animation: flank 5s linear infinite;
}

@-moz-keyframes flank
{
0%{-moz-transform:rotate(0deg)}
100%{-moz-transform:rotate(360deg)}
}

@-o-keyframes flank
{
0%{-o-transform:rotate(0deg)}
100%{-o-transform:rotate(360deg)}
}

@-webkit-keyframes flank
{
0%{-webkit-transform:rotate(0deg)}
100%{-webkit-transform:rotate(360deg)}
}

@keyframes flank
{
0%{transform:rotate(0deg)}
100%{transform:rotate(360deg)}
}

/* Logo walks back and forth */
#site_contact
{
position:relative;
left: 0px;
-moz-animation: walk 10s linear infinite;
-o-animation: walk 10s linear infinite;
-webkit-animation: walk 10s linear infinite;
animation: walk 10s linear infinite;
}

@-moz-keyframes walk
{
0%{left:0px;-moz-transform:scale(0,1)}
25%{-moz-transform:scale(1,1)}
50%{left:900px}
75%{-moz-transform:scale(-1,1)}
100%{left:0px;-moz-transform:scale(0,1)}
}

@-o-keyframes walk
{
0%{left:0px;-o-transform:scale(0,1)}
25%{-o-transform:scale(1,1)}
50%{left:900px}
75%{-o-transform:scale(-1,1)}
100%{left:0px;-o-transform:scale(0,1)}
}

@-webkit-keyframes walk
{
0%{left:0px;-webkit-transform:scale(0,1)}
25%{-webkit-transform:scale(1,1)}
50%{left:900px}
75%{-webkit-transform:scale(-1,1)}
100%{left:0px;-webkit-transform:scale(0,1)}
}

@keyframes walk
{
0%{left:0px;transform:scale(0,1)}
25%{transform:scale(1,1)}
50%{left:900px}
75%{transform:scale(-1,1)}
100%{left:0px;transform:scale(0,1)}
}
}
