css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.herozerogame.com") {



#ipbwrapper {

    background:url(http://kolyan.net/uploads/posts/2012-01/1327630286_00a.jpg) no-repeat center center fixed !important;

}


#content {

    background: -moz-linear-gradient(-45deg,  rgba(149,149,149,1) 0%, 
rgba(13,13,13,1) 46%, rgba(1,1,1,1) 50%, rgba(10,10,10,1) 53%, 
rgba(78,78,78,1) 76%, rgba(56,56,56,1) 87%, rgba(27,27,27,1) 100%) !important;
    position: relative !important;
    left: 175px;
    color:#000!important;
    -moz-border-radius:10px !important;
    margin:2px !important;
    border:1px solid !important;
    border-color:#000 !important;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px !important;
    text-shadow:#000 0px 0px 0px;
    
}

#header_bar {

    background: -moz-linear-gradient(-45deg,  rgba(149,149,149,1) 0%, 
rgba(13,13,13,1) 46%, rgba(1,1,1,1) 50%, rgba(10,10,10,1) 53%, 
rgba(78,78,78,1) 76%, rgba(56,56,56,1) 87%, rgba(27,27,27,1) 100%) !important;
    text-shadow:#000 2px 2px 2px;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px !important;
    -moz-border-radius-bottomleft:10px !important;
    -moz-border-radius-bottomright:10px !important;
    
}

#community_app_menu > li > a {

    background: -moz-linear-gradient(-45deg,  rgba(149,149,149,1) 0%, 
rgba(13,13,13,1) 46%, rgba(1,1,1,1) 50%, rgba(10,10,10,1) 53%, 
rgba(78,78,78,1) 76%, rgba(56,56,56,1) 87%, rgba(27,27,27,1) 100%) !important;
    position: relative !important; top: 1px;
    color: white !important;
    text-shadow: silver 2px 2px 4px !important;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px !important;
    -moz-border-radius-topleft:10px !important;
    -moz-border-radius-topright:10px !important;

}

#community_app_menu > li.active > a {

    color:silver !important;
   
}


.maintitle {

    background: -moz-linear-gradient(-45deg,  rgba(149,149,149,1) 0%, 
rgba(13,13,13,1) 46%, rgba(1,1,1,1) 50%, rgba(10,10,10,1) 53%, 
rgba(78,78,78,1) 76%, rgba(56,56,56,1) 87%, rgba(27,27,27,1) 100%) !important;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px inset!important;
    border-top: 2px solid !important;
    border-color:black !important;
    -moz-border-radius-topleft:10px !important;
    -moz-border-radius-topright:10px !important;

}    

#search_wrap {

    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px !important;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px inset!important;
    -moz-border-radius:10px !important;
  
}

.general_box h3 {

    background: -moz-linear-gradient(-45deg,  rgba(149,149,149,1) 0%, 
    rgba(13,13,13,1) 46%, rgba(1,1,1,1) 50%, rgba(10,10,10,1) 53%, 
    rgba(78,78,78,1) 76%, rgba(56,56,56,1) 87%, rgba(27,27,27,1) 100%) !important;
    color: silver !important;
    font-weight: bold !important;
    -moz-border-radius:8px !important;
    border: 1px solid !important;
    border-color:black !important;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px !important;

}

td.short.row2.altrow {

   background: -moz-linear-gradient(-45deg,  rgba(149,149,149,1) 0%, 
   rgba(13,13,13,1) 46%, rgba(1,1,1,1) 50%, rgba(10,10,10,1) 53%, 
   rgba(78,78,78,1) 76%, rgba(56,56,56,1) 87%, rgba(27,27,27,1) 100%) !important;
   -moz-border-radius:15px !important; 
   border:none !important;
}

.input_text, .ipsTagBox_wrapper {

    -moz-border-radius:15px !important;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px inset !important;
    border:none !important;

}

div.ipsBox.table_wrap, tr.unread, .ipsSideBlock {
    
    border:none !important;
    -moz-border-radius:15px !important;
    -moz-box-shadow:rgba(0,0,0,0.8) 2px 2px 3px inset !important;
    
    
    
}

div.ipsBox_container, tbody, table.ipb_table, .ipsSideBlock h3 {
    
    border: none !important;
    background-color: silver !important;
    -moz-border-radius:15px !important;
       
}


}
