css:
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document domain("imo.im") {
html{color:#EEE!important}
.convlog,input,textarea,select{color:#FFF}
input,textarea,select{padding:3px!important;padding-right:5px!important}
input,.convlog .convlogitem.bk_old{background-color:#3d3d3d!important}
.sprite_icn_search_2{display:none}
.convlog .ts-container,.convlog .ts{padding-right:5px!important}
a{color:#E48000}
img[src="assets/icn_default.png"],img:hover[src="assets/icn_default.png"]{opacity:0!important}
#footer,#mnp-panel,#header ul,#header img.sprite_logo_small_2,#header .history-search-container .history-search-button,div.primary-button.sprite_btn_send{display:none!important}
div{border-top-color:#333;border-left-color:#333;border-right-color:#333;border-bottom-color:#333}
.content{border-left:1px solid #333}
div#header{height:22px!important}
div#buddypanel div.blist-container {top:0!important}
div#buddypanel div.options-container{right:160px!important}
div#buddypanel div.blist-container .buddyitem .buddyimg{margin:0 2px!important}
div#buddypanel div.blist-container .buddyitem span.statusdisplay{font-size:10px!important;margin-left:4px!important;position:relative;display:inline;overflow:hidden}
div#buddypanel{z-index:1;border:none!important}
div#buddypanel div.blist-container .buddyitem.active{background-color:#555!important}
div#buddypanel div.blist-container.icon_view .buddyitem .control_container .control_buttons .control_button{margin-right:0!important}
div#buddypanel div#buddysearch{-webkit-box-shadow:none;box-shadow:none;-moz-box-shadow:none;border-bottom:none;background-color:transparent!important;display:none!important}
.inner-shadow{-webkit-box-shadow:none;-moz-box-shadow:none;box-shadow:none}
div#buddypanel .collapsed{color:#CCC}
div#buddypanel span.dropdown{margin:0 8px 0 0}
div.buddygroup{border:3px solid #333!important;box-shadow:0 0 6px #FFF;-webkit-box-shadow:0 0 6px #FFF;-moz-box-shadow:0 0 6px #FFF;border-radius:12px;-webkit-border-radius:12px;-moz-border-radius:12px;right:12px;bottom:12px;margin:2px 12px 10px!important}
#body{top:0!important;bottom:0!important}
div#body div#main.with_mnp{left:0!important}
#header{left:auto!important;width:100%!important;pointer-events:none;background:transparent!important;border:none!important}
#buddypanel{margin-top:40px!important}
div.bar{font-weight:normal!important}
div.bar.white{color:#CCC!important;text-transform:lowercase!important}
.convlog .ms,.buddygroup .bar.white,.buddygroup .bar.white .title,.buddygroup .bar.white span{font-size:14px;line-height:20px;color:#ccc}
.buddygroup .bar.white,.buddygroup .bar.white .title,.buddygroup .bar.white span{font-size:12px!important}
.buddygroup .bar.white{height:20px!important}
.button img{margin:3px!important}
.sprite_icn_buddy_more{margin-right:6px!important}
div#body,div#header,.convpanel .convbuttons .convbutton,div.bar.white,div#buddypanel div.blist-container .buddyitem{border-bottom:none!important}
.convpanel .convbuttons .convbutton{height:14px!important;line-height:14px!important;border:none!important;background-color:transparent!important}
.convpanel .convbuttons .convbuttons_end{height:14px!important;border:none!important}
#header .history-search-container .history-search-input{margin-top:4px;height:20px;line-height:20px;padding:0;width:inherit;opacity:0.1;display:none}
#header .history-search-container .history-search-input:hover{display:none;opacity:1}
#account-bar,#header .history-search-container .history-search-input{pointer-events:auto}
div#account-bar div.account-gear.open{background-color:#555}
div#buddypanel div#buddysearch{height:27px}
div#buddypanel div#buddysearch #buddysearch_input_div input{height:16px;line-height:16px;border:none}
.content div.tab-menu div.tabs div.titlebar-tabbed{width:118px!important}
#account-gear span{color:#ccc}
.buddygrouplist .buddyitem{height:auto!important;line-height:20px!important;font-size:11px!important}
.buddygrouplist .buddyitem .imgwrap{height:auto!important;width:20px}
.buddygrouplist .buddyitem .imgwrap img.icon{height:20px!important;width:20px!important;line-height:20px!important}
.convpanel div.convmain{right:12px!important}
.convlog .convlogitem.me,.convpanel div.convmain div.convbottom div.convinput{background-color:#3D3D3D!important}
.convpanel div.convmain div.convbottom div.convinput{margin:5px 0 5px 5px!important;border-radius:6px!important;-webkit-border-radius:6px!important;-moz-border-radius:6px!important;padding:3px!important;padding-left:5px!important}
.convlogitem.me span{color:#E48000!important;font-style:normal!important}
.convlogitem.bk0{background-color:transparent!important}
#buddypanel .blist-container,.language_selector .button-selected,.language_selector .button-normal,.account-item{font-size:12px!important}
#buddypanel,#buddysearch,#account-bar{width:200px!important}
#buddysearch{position:fixed!important}
#buddysearch #buddysearch_input_div{width:80%!important}
#buddysearch #buddysearch_input_div input{width:126px!important}
#thinui #thinui-container{width:200px;background-color:#494949}
#main{right:180px!important}
.tabbar{z-index:1!important;border:none!important}
.tabbar .right-more-indicator,.tabbar .left-more-indicator{background-color:#333!important}
.tabbar .visible-tab-list{border:none!important}
.tabbar .visible-tab-list.no-arrows{background-color:#3D3D3D!important;right:80px!important}
div.titlebar-tabbed{background-color:#555!important;border:1px;border-radius:6px;-webkit-border-radius:6px;-moz-border-radius:6px;border-color:#555!important}
div.titlebar-tabbed.focused{background-color:#E48000!important;border-color:#E48000!important}
.tabbar .menu-button{border:none!important;background:none!important;color:#FFF!important;font-size:10px;top:2px}
.sprite_icn_close{background-color:transparent!important}
.buddyitem .icon{width:42px!important}
.buddyitem .control_container .control_buttons .control_button{margin-top:2px!important}
.convinput{right:4px!important}
.convpanel .convbuttons{top:0;background-color:#333!important;border-bottom:1px solid #333!important}
div.avhover-outer{top:16px!important;border:none;background-color:#333}
.content .windowbody{right:16px!important}
div.history-search-widget div.bottom div.browser div.result .date,div.history-search-widget div.bottom div.browser div.result .message{color:white!important}
div.history-search-widget div.input-controls{border:none!important;background-color:#333!important;color:white}
div.history-search-widget div.bottom div.browser div.preview-browser{border-color:#333!important}
div.history-search-widget div.bottom div.resize,div.history-search-widget div.bottom div.browser div.result.odd{background-color:#3D3D3D!important}
div.history-search-widget div.bottom div.browser div.result.even {background-color:#555!important}
div.history-search-widget div.bottom div.browser div.result:hover.even{background-color:#E48000!important}
div.history-search-widget div.bottom div.browser div.preview-browser div.nav-container {background-color:#333!important;border:none!important;color:white!important}
div.history-search-widget div.bottom div.viewer div.convmain{background-color:#333!important}
div.history-search-widget div.bottom div.viewer div.convmain div.delete,div.history-search-widget div.bottom div.viewer div.convmain div.delete:hover{background-color:transparent!important;border:none!important;color:#E48000}
div.convprofile div.convprof_details div.buttons div.convprof_button{color:black!important}
.preference-widget .pref-section span.pref-bool-text,.modal-popup-container{color:black}
div#body,div#buddypanel,div#header,div#buddypanel .collapsed,.convpanel .convbuttons .convbutton,.content,.convlog,#mnp-panel,div#buddypanel div.blist-container div#blist div.buddygroup div.buddygrouplist div.buddyitem:hover,div#buddypanel div.blist-container div#blist div.buddygroup div.buddygrouplist div.buddyitem.selected,.convpanel div.convmain div.convbottom{background-color:#333!important}
div#buddypanel div.blist-container div#blist div.buddygroup div.buddygrouplist div.buddyitem:hover,div#buddypanel div.blist-container div#blist div.buddygroup div.buddygrouplist div.buddyitem.selected,div#buddypanel div.bar:hover{background-color:#E48000!important}
}
