css:
body {
	font-family: 'Segoe UI', Verdana, Arial, Helvetica, sans-serif;
	font-size: .8em;
	background: #ccc;
	color: #444
}
.main {
	margin: 0 auto
}
.main > .gwt-HTML h2 {
	opacity: 1;
	-webkit-animation: wait 1s steps(1, end);
	-moz-animation: wait 1s steps(1, end);
	animation: wait 1s steps(1, end)
}
h1 {
	margin: 1em 0
}
.gwt-DialogBox {
	position: fixed !important;
	top: 1em;
	height: 5em !important;
	clip: initial !important
}
.gwt-DialogBox:hover {
	height: auto
}
#banner {
	display: none
}
.gwt-PopupPanelGlass {
	display: none !important
}
.logo img {
	-webkit-filter: drop-shadow(0 0 1em #fff);
	-moz-filter: drop-shadow(0 0 1em #fff);
	filter: drop-shadow(0 0 1em #fff)
}
.titleheader {
	text-shadow: 0 0 .7em #fff
}
.topbar {
	left: auto;
	padding: .3em 1em;
	-webkit-border-radius: .4em;
	-moz-border-radius: .4em;
	border-radius: .4em;
	-webkit-box-shadow: 0 0 .5em #fff;
	-moz-box-shadow: 0 0 .5em #fff;
	box-shadow: 0 0 .5em #fff
}
.loginpanel {
	margin: 3em;
	background: #ddd;
	-webkit-border-radius: .4em;
	-moz-border-radius: .4em;
	border-radius: .4em;
	-webkit-box-shadow: 0 0 1em #000;
	-moz-box-shadow: 0 0 1em #000;
	box-shadow: 0 0 1em #000
}
.menuitem, .menuitemselected {
	padding: .5em;
	text-align: center;
	border: 1px solid #fff;
	-webkit-border-radius: .3em;
	-moz-border-radius: .3em;
	border-radius: .3em;
	-webkit-box-shadow: 0 0 .4em #000;
	-moz-box-shadow: 0 0 .4em #000;
	box-shadow: 0 0 .4em #000
}
.menuitemselected {
	background: #eee;
	-webkit-box-shadow: 0 0 1em #000;
	-moz-box-shadow: 0 0 1em #000;
	box-shadow: 0 0 1em #000
}
.submenu {
	overflow: hidden
}
.sublink {
	float: none;
	display: inline-block;
	line-height: 2.2em;
	text-indent: 1em;
	background-color: #ddd;
	margin: .5em;
	padding: .4em 1em .4em 2.5em;
	-webkit-border-radius: .2em;
	-moz-border-radius: .2em;
	border-radius: .2em;
	-webkit-box-shadow: 0 0 .3em #000;
	-moz-box-shadow: 0 0 .3em #000;
	box-shadow: 0 0 .3em #000
}
.sublink a {
	display: block
}
.feature {
	padding: 2em;
	background: #eee;
	-webkit-border-radius: 1em;
	-moz-border-radius: 1em;
	border-radius: 1em;
	-webkit-box-shadow: 0 0 1em #000;
	-moz-box-shadow: 0 0 1em #000;
	box-shadow: 0 0 1em #000
}
@-webkit-keyframes wait {
	from {
		opacity: 0
	}
	to {
		opacity: 1
	}
}
@-moz-keyframes wait {
	from {
		opacity: 0
	}
	to {
		opacity: 1
	}
}
@keyframes wait {
	from {
		opacity: 0
	}
	to {
		opacity: 1
	}
}
