css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("syosetu.org") {
  #container,#page,ul.nl {
    width: auto !important;
    border: none !important;
    border-left: 0px !important;
    border-right: 0px !important;
  }
  #maind{
    width: 100% !important;
  }
  div.ss {
    width: 92% !important;
    margin-left: auto !important;
    margin-right: auto !important;
    line-height: 150% !important;
  }
  #header {
    padding: 0 !important;
    font-size: 120% !important;
  }
  .siteTitle,
  .guide,
  #footer,
  .spotlight,
  #content #nav,
  #header .nl li:nth-of-type(2),
  #header .nl li:nth-of-type(6){
    display: none !important;
  }
  input[name="rtext"] {
    width: 8em !important;
  }
  body,
  #main,
  #nav,
  #aside,
  #maind,
  div.ss{
    font-size: 100% !important;
  }
  table,tr, td {
    font-size: 100% !important;
  }
  #main{
    float: left !important;
    width: 80% !important;
  }
  #nav {
    float: left !important;
    width: 20% !important;
  }
  #content {
    width: 70% !important;
  }
  #content #main{
    width: 100% !important;
  }
  #aside div.section {
    border-right: none !important;
  }


  #aside {
    margin-left: 0px !important;
    width: 30% !important;
  }
  #maind + div {
    display: none !important;
  }
  iframe {
    width: 100% !important;
  }
  #main div.pageTitle {
    margin: 0 5px 5px 0 !important;
  }
  #main div.section {
    margin: 0 5px 2em 10px !important;
  }
  #content hr.clear {
    clear: both !important;
    width: auto !important;
  }
}
