@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("flickr.com") {
/* STYLE3 ================================================================================================= */
/* ========  FLICKR - 3 - NEW -  Flickr WideScreen - Hidden Top NavBar v.2 - OK STANDALONE ================ */
