/* SUPP FLICKR LOGO */
/* GREEN ==== */
.main-logo ,
.view.global-nav-view .global-nav-restyle .global-nav-content .flickr-logo-container ,
.view.global-nav-view .global-nav-restyle .global-nav-content .flickr-logo-container .main-logo ,
