css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tumblr.com") {

/* Follow me at glacier-coast.tumblr.com */

body{background: url('http://i49.tinypic.com/yordk.png') !important;
background-size: 100% !important;
background-attachment: fixed !important;
background-color:#000000 !important;
background-position: 0px -100px !important;
margin:auto !important;}

a {color: #fff !important;}
.post_content{color: #6984B2 !important;}
	.defaultSkin table.mceLayout tr.mceLast td style {}
	 td.mceToolbar{
	 background-color: #2D5378 !important;
	}
.source_url{color: #4F73B2 !important;}
#search_query.popover_button.with_unread {color: #fff !important;}
#logo, #search_field, #popover_tracked_tags {margin-top: 44px !important;}
.selection_nipple {
background-image: url('http://i50.tinypic.com/358b19h.png') !important;
background-repeat: no-repeat !important;
height: 30px !important;
width: 13px !important;
top: -15px !important;
padding-top: 66px !important;

opacity: 1 !important;
z-index:-1 !important;

}
div.l-header{margin-bottom: 30px !important;}
divdiv.l-content.clearfix {background-color: #fff !important;}
html .mceContentBody {
max-width: 584px;
background-image: url('http://i48.tinypic.com/34gxau0.png') !important;
background-position: 0px -36px !important;
color: #fff !important;
}
pre{background-color: rgba(8,29,65,.87) !important;}
    .post{background-color: rgba(8,29,65,.87) !important;}
.post_content{background-color: rgba(8,29,65,.87) !important;}
#posts .post.new_post {background-color: #1A3564 !important;
background-image: url('http://i50.tinypic.com/34o67b9.png') !important;
opacity: 1 !important;
background-position: 0px -20px !important;
margin-left: -105px !important;
margin-top: -20px !important;
padding-top: 20px !important;
height: 350px !important;
width: 890px !important;
box-shadow: none !important;
}
.new_post_label_icon, .new_post_label {opacity: .00001 !important; 
margin-top: -3px!important;

}
.new_post_label_icon:hover {opacity: 1 !important; 
margin-bottom: 5px!important;

}
#post_controls_avatar {display: none !important;}
.new_post_label:hover {opacity: 1 !important;
margin-bottom: 5px!important;}
#posts .post .arrow {display: none !important;

}
#right_column {margin-top: 390px !important;
background-color: rgba(8,29,65,.0) !important;}
.controls_section{
border: none !important;
}
.controls_section:first-child{margin-top: -33px !important;}
.controls_section li{background-color: rgba(8,29,65,.74) !important;
border: none !important;}
.post:nth-child(2){
margin-top: -4px !important;}
li.post{
margin-left:-100px !important;
background-color: rgba(8,29,65,.87) !important;
border: none !important;
box-shadow: inset 0px 0px 8px #081D41 !important;}

.l-content{background-color: #1A3564 !important;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: #1A3564 url('http://i47.tinypic.com/2d0xgd4.png') repeat fixed 0px 0px !important;
border: 2px solid #081D41 !important;
background-position: -715px 450px !important;}
#posts .post .post_avatar{display: none !important;

}

.notes a, .note.reblog.hover, .notes_control .notes_hide_link:hover, .more_notes_link_container:hover, .popover_menu_item a, .notes, .note{color: #21314B !important;}
li.notification{background-color: rgba(8,29,65,.74) !important;}
.logo_anchor {display:none !important;}
#search_field {margin-top:0px !important;}
.search_form_row{background-color:#000 !important;}
a.name{color:#0A2046 !important;}
#left_column{background-color:rgba(0,0,0,0)}
#right_column{background-color:rgba(62,167,142,.5)}
.l-header{background-color:#0D234A;}
}
