css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://home.myspace.com/index.cfm?fuseaction=user") {

body {
    background-color: #fff !important;
}

#wrap {
    min-height: inherit !important;
    background-color: transparent !important;
}

div[align="center"][style="height: 90px;"] {
    display: none !important;
}

#googlebar {
    display: none !important;
}

#mainContent {
    position: relative !important;
}

#grayboxrounded {
    display: none !important;
}

#col2 {
    width: 500px !important;
}

#col2 div.middle {
    max-height: 500px !important;
}

#col3 {
    display: none !important;
}

#friendspace {
    position: absolute !important;
    top: 20px !important;
    right: 10px !important;
    width: 240px !important;
}

#friendspace #friends * {
    margin: 0 !important;
    padding: 0 !important;
}

#friendspace div.middle {
    max-height: inherit !important;
    overflow: visible !important;
}

#friendspace div.friend {
    width: 50px !important;
    height: 50px !important;
}

#friendspace div.friend a {
    color: #fff !important;
    font-size: 0px !important;
}

#friendspace div.friend img {
    width: 51px !important;
    height: 51px !important;
}

#friendspace div.friend .ImgOnlineNow {
    position: relative !important;
    top: -14px !important;
    width: 50px !important;
    height: 14px !important;
    background-color: #fff !important;
    opacity: 0.9 !important;
}

#footer {
    display: none !important;
}

}
