css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("downg.com") {

/* 去广告，默认禁用，尽量使用abp */
/* [class*=" ad-b"],[class="cp sponsor"]{display: none !important;} */

/* 首页中部空白修正 */
div.cp.softwaretable{height: 350px !important;}

/* 最新更新重排 */
.part1>.col2 {width: 700px !important;}
.part1>.col3>.cp-gray {height:480px !important;}

/* 下载页重排 */
.software-view {width: 100% !important;}

/* 下载按钮重排，3×3 */
.download-list {width: 480px !important;}

/* 评论适合宽度 */
.comment-list {width: 100% !important;}

/* 搜索结果重排 */
table>tbody>tr>td[width="785px"] , .sskuang {width: 99.8% !important;}
table>tbody>tr>td[width="165px"] {display: none;}

/* 更多软件页面重排 */
.col5>div>div[style*="width:740px;"] {width: 99.8% !important;}
.col5>div>div[align*="right"] {display: none;}
}
