css:
@-moz-document domain("wikipedia.org"), url(http://en.wikipedia.org/wiki/Wikipedia) {
#content {
  -webkit-box-shadow: 0px 0px 80px #999;
  -moz-box-shadow: 0px 0px 80px #999;
  box-shadow: 0px 0px 80px #999;

  position: absolute;
  top: 3em !important;
  left: 15% !important;
  z-index: 1;

  width: 70% !important;
  min-width: 60ex !important;
  max-width: 120ex !important;

  margin: 0px !important;

  background: -webkit-gradient(
    linear,
    left top,
    left bottom,
    color-stop(0.0, rgb(250,248,220)),
    color-stop(0.05, rgb(255,255,255)),
    color-stop(0.6, rgb(250,248,220))
  )!important;
  background: -moz-linear-gradient(
    center top,
    rgb(250,248,220) 0%,
    rgb(255,255,255) 5%,
    rgb(250,248,2205) 60%
  ) !important;
}

#content h2 {
  padding-top:50px !important;
}

#content h3 {
  margin-top:40px !important;
}

#content p {
  font-size: 12pt;
}

#content li {
  font-size: 12pt;
}

#firstHeading {
  font-size: 30pt !important;
  color:#c5bc98 !important;
  margin: 1em !important;
  text-shadow: 1px 1px 1px #666;
  text-align:center;
}

.thumbinner {
  margin:10px 20px 10px 20px !important;
}



.ambox {
  display:none !important;
}



.mediawiki {
  background-image: none !important;
  background-color:#dfddc8 !important;
}

#mw-page-base {
  display: none !important;
}

#mw-navigation {
  position: absolute !important;
  top: 0em;
  left: 0ex;
  width: 100%;
  height: auto;
}

#p-personal {
  position: absolute;
  top: 0px;
  right: 0px;
  width: 15%;
  margin: 5px;
  padding: 0px !important;
}

#p-personal ul {
  width: auto;
  height: auto;
}

#p-personal li {
  float: right;
  clear:both;
  margin-left: 1ex;
  margin-right: 1ex;
}

#left-navigation {
  display: none !important;
}



#right-navigation {
  position: absolute;
  left: 15%;
  width: 70%;
  margin: 0px;
}

#p-views {
  display: none !important;
}

#p-cactions  {
  display: none !important;
}

#p-search {
  width: 100%;
  margin: 0px;
}

#simpleSearch {
  margin-left: auto !important;
  margin-right: auto !important;
}




#mw-panel {
  position: static !important;
  min-width: 150px !important;
  width: 15% !important;
  top: 0px !important;
  margin: 0px !important;
  padding: 0px !important;
}

#mw-panel.collapsible-nav .portal {
  background: none !important;
}

#p-logo {
  display: none;
}

#p-lang {
  margin: 5px !important;
  padding: 0px !important;
}

#p-navigation {
  display: none !important;
}

#p-coll-print_export {
  display: none !important;
}

#p-tb {
  display: none !important;
}

#p-interaction {
  display: none !important;
}

#siteSub {
  display: none !important;
}

#mw-articlefeedback {
  display: none !important;
}

#mw-articlefeedbackv5 {
  display: none !important;
}

#footer {
  display: none;
}


#content #toc.toc {

background-color: #CCC !important;
  display: block !important;
  position: fixed ;
  top: 0;
  left: 0 ;
  width: 6px;
  /*height: 93% ;*/
  border: none !important;
  overflow: hidden;
  z-index: 9999;
  text-indent: -9999px ;
  border-right: 2px solid #BBB !important;
  padding: 0 !important;
}

#content #toc.toc:hover {
  background-color: rgba(0,0,0,.82) !important;
  width: auto; 
  text-indent: 0;
  overflow-y: auto;
  padding: .6em !important;
}


#content #toc.toc H2 {color: #EEE !important;}
#content #toc.toc a  {color: #BACFF2 !important;}
#content #toc.toc a:visited {color: #DDC6F2 !important;}
#content #toc.toc a:hover {color: #FFE900 !important;}

#toc.toc .toctoggle {display: none !important;}
}
