.fh td, .fd td {
    background: none repeat scroll 0 0 #000000 !important;
    padding-left: 4px !important;
    }

#se_content {
    background-color: #000000 !important;
    border: 1px solid #000000 !important;
    border-radius: 10px !important;
    }

.quick_item_selected {
    background-color: #000000 !important;
    }

.quick_item_header {
    background-color: #000000 !important;
    }

#header {
    background: transparent !important;
    }

.tabnav {
    border: 1px solid transparent !important;
    }

.tabnav a {
    background-color: transparent !important;
    border-top: 2px solid transparent !important;
    }

.tabnav a:hover {
    background: rgba(00,00,00,0.5)!important;
    border-radius: 2px !important;
    }

.remove_on_search center a, .remove_on_search, .footer a, center a, artikelmain_wrap a, #blogs-functions a, small a, span a,#se_content a, a.at, a.createThread {
    color: #FA0000 !important;
    }

#chat_list a, #fl a u, #fl ua {
    color: #fff !important;
    }

