    opacity: 0.95;
    }

.remove_on_search center a:hover,.remove_on_search:hover, .footer a:hover, center a:hover, .artikelmain_wrap a:hover, #blogs-functions a:hover, small a:hover, span a:hover,#se_content a:hover,a.at:hover {
    color: #FFFFFF !important;
    text-shadow: 0 0 10px #fff !important;
    }

#chat_messages {
    background: rgba(00,00,00,0.7);
    }

a[href="javascript:pn('Verwaltung', '29904')"] {
    color: #FCFCFC !important;
    }

a[href="javascript:pn('system', '28834')"] {
    color: #FCFCFC !important;
    }

.blogs-prevpost {
    background-color: rgba(0,0,0,0.6);
    border-radius: 5px 5px;
    }

.blogs-prevpost.even {
    background-color: rgba(0,0,0,0.6);
    border-radius: 5px 5px;
    }
    
.voteup {
    background-color: rgba(0,0,0,0.6);
    border-radius: 5px 5px;
    }

.Voting .ui-widget-content .ui-state-default {
    background: red !important;
    border-radius: 5px 5px !important;
    }

.Voting-bar-filling {
    background: black !important;
    }
  
.Voting-voted .Voting-bar-filling  {
    background: red !important;
    }

div.post.warn {
    background: none !important;
    background-color: rgba(152,0,0,1.0) !important;
    box-shadow:inset 90px 0px 120px #3A3A3A !important;
    border: 2px dotted  #ff0000 !important;
    }
