css:
@-moz-document url-prefix('http://www.nyaa.eu/')
{

#topbar
{
	background-image: none !important;
	background-color: #333 !important;
	position: relative !important;
	border: none !important;
	font-family: 'Segoe UI' !important;
}

#navbar
{
    width: 48% !important;
}

#searchcontainer
{
    width: 50% !important;
}

div.content
{
	background-color: rgba(255, 255, 255, 0.95) !important;
	position: relative !important;
	top: -45px !important;
	width: 96% !important;
	border: 2px solid #666 !important;
	font-family: 'Segoe UI' !important;
	border-radius: 0px !important;
	border-top: none !important;
	padding: 10px !important;
}

#chatbox
{
	position: relative !important;
	top: -50px !important;
	width: 100% !important;
	border: none !important;
}

.tlisthead {
    background-color: #BBB !important;
    height: 28px !important;
}

.tlisthead th {
    vertical-align: middle;
}

.tlisthead .tlistthone {
    padding: 0 8px !important;
    width: 8% !important;
}

.tlisthead .tlistthtwo:before {
    content: "Name"
}

.tlistsize {
    border-left: 1px solid #BBB !important;
    border-right: 1px solid #BBB !important;
}

.tlistrow {
    border-top: 1px solid #999 !important;
}

.trusted.tlistrow
{
	background-color: #c5e2cc !important;
}

.remake.tlistrow
{
	background-color: rgba(100, 50, 50, 0.5) !important;
}

.aplus.tlistrow
{
	background-color: rgba(50, 100, 50, 0.5) !important;
}

.tlistrow a, .tlistrow td {
    font-weight: normal !important;
}

.content a
{
	text-decoration: none !important;
	color: rgb(0, 0, 0) !important;
}

#tabnav > li > a
{
	color: #ffffff !important;
}


#tabnav > li > a:hover
{
	color: rgba(255,255,255,0.8) !important;
}

a:hover
{
	color: rgba(0,0,0,0.5) !important;
}

.page {
    border: 1px solid #AAA !important;
}

.page:hover
{
	background-color: #EEE !important;
	color: #000 !important;
}

div[style="float:left;width:100%;"], div[style="float:right;width:11.9%;"], .main > div[style="float:left;"], a[target="_blank"]
{
	display: none !important;
}

.tinfodescription, .comment {
    border-radius: 0px !important;
    border: 2px solid #999 !important;
}

.tinfocontainer > div:nth-child(3), .tinfocontainer > div:nth-child(5), .tinfocontainer > div:nth-child(6), .tinfocontainer > div:nth-child(7), .tinfocontainer > br:nth-child(11), .tinfocontainer > br:nth-child(12), .tinfodownloadtxtbutton {
    display: none;
}

.tinfodownloadbutton, .tinfodescription {
    display: block !important;
    clear: both;
}

.tlisticon {
    padding: 3px 6px !important;
}

.tinfodownloadbutton {
    width: auto !important;
}

.tinfodownloadbutton > a {
    background: url('http://i.imgur.com/gd85z.png') 0px no-repeat;
    height: 36px;
    width: 137px;
    display: block;
}

.tinfodownloadbutton img {
    display: none;
}

.tinfofiletable {
    border: 2px solid #999 !important;
    border-radius: 0px !important;
    padding: 4px !important;
}

.fileentryname {
    padding: 1px 0 !important;
    width: 100% !important;
    border-bottom: 1px solid #CCCCCC !important;
}

.fileentrysize {
    padding: 1px 0 !important;
    border-bottom: 1px solid #CCCCCC !important;
}

.tinfoshowhidefiles {
    margin: 6px 0 0 !important;
    display: block !important;
    font-weight: normal !important;
}

.tlistpages {
    margin: 20px auto 10px !important;
}

.tlistsortorder {
    padding: 8px 0 !important;
}

.torrentcats {
    padding-left: 0 !important;
}

.tlistdownload > a {
    background: url('http://i.imgur.com/qXZ0r.png') !important;
    display: block !important;
    height: 16px !important;
    width: 16px !important;
}

.tlistdownload img {
    display: none !important;
}

}
