css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(mobile01.com) {

* {
	font-family: 微軟正黑體 !important;
}

div[class="widget panel-group"],
div[class="widget fast-ad"],
div[class="ad-a"],
div[class="ad-a1"],
div[class="ad-a2"],
div[class="ad-b"],
div[class="ad-c"],
div[class="ad-d"],
div[class="text-ad"],
div[class="double_ad_l"],
div[class="widget article"],
div[class="widget"],
div[class="catbar-ad"],
div[class="double_ad_r"]{
	display: none !important;
}

/* Header */

#header .inner {
	width: 95%;
}

#header .inner .inner {
	width: 100%;
}

#headline {
	margin-left: 0px;
}

/* 下拉選單 */
.sf-menu a {
	font-size: 1.5em;
}


#content {
	width:95% !important;
}

/* 文章標題 */
#section .main h2 {
	font-size:26px !important;
}

#section .main table .subject-text a.topic_gen {
	font-size:18px !important;
}

#section .main {
	width:95% !important;
}


/* 首頁下方區塊 */

#index-category .category {
	width:96% !important;
}

#index-category .item {
	width:49% !important;
}

#index-category .item h2 a {
	font-size: 26px;
}

#index-category .category a.group h3 {
	font-size:22px;
	line-height: 26px;
	padding-bottom: 5px;
}

#index-category .category p {
	font-size:20px;
	line-height: 24px;
	height: auto;
}

#index-category .category a.group, #index-category .category a.group:link {
	margin-bottom: 10px;
}

/* 文章清單 */

.subject-text {
	font-size:20px;
}

#maincontent div:nth-child(2) {
	display: none;
} 


/* 文章內容 */

.single-post-content {
	font-size: 1.5em;
	line-height: 1.75em
}

.single-post-author-img {
top:-12px;
	width:75px;
	height:75px;
}
.single-post-author-img div {
	width:75px;
	height:75px;
}

.pagination {
	font-size: 1.5em;
}

iframe[ width="100%"][height="480"]{
width:640px !important;
height:390px !important;
}

}
