css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cloudsdale.org") {

}

.topnav a.brand {
    background-size: auto !important;
    background-position: center top !important;
    height: 30px !important;
}

.topnav .topnav-inner {
    height: 30px !important;
}

.topnav ul.nav.pull-right {
    height: 30px !important;
}

.topnav ul.nav.pull-right li {
    height: 30px !important;
    border-left: thin solid #99BBDD;
    border-right: thin solid #3399CC;
}

.topnav ul.nav.pull-right li a span {
    line-height: 20px !important;
    text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.4) !important;
}

body {
    line-height: 14px !important;
    top: 30px !important;
}

.main-container {
    top: 30px !important;
}

.chat-message-head {
    line-height: 20px !important;
}

li.sidebar-item span.sidebar-item-notification {
    margin: -7px 0 0 !important;
}

.chat-message {
    padding: 5px !important;
}

.sidebar {
    width: 230px !important;
}

body.with-active-session .main-container {
    margin-left: 230px !important;
}

.view-container.cloud-container .fixed-container {
    width: 230px !important;
}

.view-container.cloud-container.with-open-drawer .float-container {
    margin-right: 230px !important;
}

a.drop-big-title {
    margin-left: 0 !important;
}

a.drop-big-preview {
    float: none !important;
}

.sidebar-header-link {
    margin: 0 !important;
    padding: 1px 0 !important;
}

.sidebar-header {
    padding: 2px 7px !important;
}

.sidebar-input {
    border-bottom: 0px !important;
    border-top: 0px !important;
    padding: 0px !important;
    margin: -5px !important;
}

.sidebar-input form {
  margin: 0 !important;
}

li.sidebar-item a {
    padding: 0px !important;
}

li.sidebar-item span.sidebar-item-name, li.sidebar-item span.sidebar-item-notification {
    margin-bottom: 0px !important;
}

ul.cloud-drop-list {
    margin: 10px 5px !important;
}

.chat-message.role-developer .chat-message-avatar, .chat-message.role-founder .chat-message-avatar, .chat-message.role-donor .chat-message-avatar, .chat-message.role-admin .chat-message-avatar, .chat-message.role-associate .chat-message-avatar, .chat-message.role-legacy .chat-message-avatar, .chat-message.role-verified .chat-message-avatar {
    margin-bottom: 0px !important;
}

.chat-message.role-developer .chat-message-avatar:before, .chat-message.role-founder .chat-message-avatar:before, .chat-message.role-donor .chat-message-avatar:before, .chat-message.role-admin .chat-message-avatar:before, .chat-message.role-associate .chat-message-avatar:before, .chat-message.role-legacy .chat-message-avatar:before, .chat-message.role-verified .chat-message-avatar:before {
    margin: 0 -1px -1px !important;
}
