css:
@-moz-document domain(schuelervz.net), domain(studivz.net), domain(meinvz.net){

  body {
    color:#000000 !important;
  }

  a {
    color:#08a300 !important;
  }

  h2.clearFix, .mH, .clearFix h2 {
    background:#71eb6d none repeat scroll 0% 50% !important;
    border-bottom:1px solid #54cf4d !important;
    border-top:1px solid #08a300 !important;
    color:#08a300 !important;
    font-size:11px !important;
  }

  .RegBox h2 {
    background-color:transparent !important;
    border:medium none !important;
    margin-bottom:20px !important;
    padding:0pt !important;
    font-size:11px !important;
  }

  h3 {
    color:#08a300 !important;
  }

  input.fieldBtnSubmit, input.fieldBtnCancel {
    background:#08a300 none repeat scroll 0% !important;
    border-color:#54cf4d !important !important #0ad201 #0ad201 #54cf4d !important;
  }

  li.selected a:hover, div.activetab a:hover {
    color:#ffffff !important;
  }

  li.selected, div.activetab {
    background-color:#08a300 !important;
    color:#ffffff !important;
  }

  li.selected a, div.activetab a {
    color:#ffffff !important;
    background-color:#08a300 !important;
  }

  ul#tabBar {
    border-bottom-color:#08a300 !important;
  }

  #tabBar li:hover, div.inactivetab a:hover {
    background-color:#c6fec4 !important !important;
  }

  #tabBar li.selected:hover, div.activetab a:hover {
    background-color:#08a300 !important;
    color:#ffffff !important;
  }

  /* Kopfleiste */
   #topHeader {
    background-image:url(http://www.abload.de/img/topbgmej.png) !important;
    background-repeat:no-repeat !important;
  }

  #topNavi a:hover {
    border: 1px solid #ffefef !important;
    border-bottom: none !important;
    background:#08a300 url(http://www.abload.de/img/title-hover86n.gif) !important;
    background-repeat:repeat-x !important;
  }

  #topNavi a {
    color: #fff !important;
    border: 1px solid #0ad201 !important;
    border-bottom: none !important;
    margin:0 1px !important;
  }

  #pageHeader {
    background:#0ad201 none repeat scroll 0% !important;
    border-color: #54cf4d !important;
  }

  /*Navileiste*/
  .linkList li {
    border-bottom:1px solid #54cf4d !important;
  }

  .linkList a:hover {
    background:url(http://www.abload.de/img/title-hover86n.gif) #08a300 !important;
    color:#fff !important;
  }

  .linkList a:focus {
    background-color:#fff !important;
    color:#08a300 !important;
  }

  .friendsInfo, #GroupsSnipplet, .pinboard_Navi, .commentMetaData {
    background-color:#c6fec4 !important;
    border-bottom:1px solid #54cf4d !important;
    border-top:1px solid #08a300 !important;
  }

  #pageFooter {
    background-color:#08a300 !important;
    border-bottom:1px solid #54cf4d !important;
    border-top:1px solid #08a300 !important;
  }

  #pageFooter a {
    color: #fff !important;
    border: 1px solid #0ad201 !important;
    border-bottom: none !important;
    margin:0 1px !important;
  }

  #pageFooter a:hover {
    border: 1px solid #ffefef !important;
    border-bottom: none !important;
    background:url(http://www.abload.de/img/title-hover86n.gif) #08a300 !important;
    background-repeat:repeat-x !important;
  }

  #GroupsSnipplet ul {
    color:#08a300 !important;
  }

  .friendsSearchBox, .photosmHNavi, th.cb, th.status, th.fromName, th.fromImg, th.subject, .forummHNavi, .groupsmHNavi {
    background-color:#c6fec4 !important;
    border-bottom:1px solid #54cf4d !important;
    /* border-top:1px solid #08a300 !important; */
  }

  .threadPageCounter, .threadWrap {
    background-color:#c6fec4 !important;
    border-bottom:1px solid #54cf4d !important;
    border-top:1px solid #08a300 !important;
  }

  .pagerCont, .resultsRow, .AlbumCont, .groupsWrap {
    background-color:#c6fec4 !important;
  }

  #accordion h1 {
    background-color:#08a300 !important;
  }

  #PhotoAlbums .clearFix .pagerCont {
    background-color:#fff !important;
  }

  #content {
    border-left:1px solid #54cf4d !important;
    border-right:1px solid #54cf4d !important;
  }

  .AlbumCont, .AlbumCont .clearFix, #name, #accordion h1, .helpbox, .RegBtnContainer div, .threadWrapBorder, .createNewThread {
    border-color:#54cf4d !important;
  }

  .helpbox, .RegBtnContainer div, .threadPageCounter {
    background-color:#c6fec4 !important;
  }

  .status_new td, .wrap {
    background-color:#dfffde !important;
  }

  .wrap {
    border: 1px solid #54cf4d !important;
  }

  /* Werbung entfernen */
  #FLASH_AD {
    display:none !important;
  }
}
